import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';


const ItemList = () => {
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/item/seller/7afa80cc-e278-4a38-b41a-9be258b1fe99');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const filteredItems = items.filter(item =>
    item.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Item List</h1>
      <div>
        <label htmlFor="search">Search by Title: </label>
        <input
          type="text"
          id="search"
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>
      <ul>
        {filteredItems.map(item => (
          <li key={item.id}><Link to={`/items/${item.id}`}>
            <div className='container'>
              <div className='row m-2'>
                <div className='col-4'></div>
                <div className='col-2'>
                  <img
                    class="img-fluid img-thumbnail"
                    src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                    alt={item.title}
                    // style={{ width: '400px', height: '400px' }}
                  />
                </div>
                <div className='col-2'></div>  
                <div className='col-4'>
                  <p>{item.title}</p>
                </div>       
                                       
              </div>
            </div>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
