// ImagePreviewModal.js
import React from 'react';
import Modal from 'react-modal';

const ImagePreviewModal = ({ isOpen, onClose, imageSrc, altText }) => {
  return (
    <Modal isOpen={isOpen} onRequestClose={onClose} style={modalStyles}>
      <div style={{ position: 'relative' }}>
        <span
          style={{ position: 'absolute', top: '10px', right: '10px', cursor: 'pointer', fontSize: '20px' }}
          onClick={onClose}
        >
          &times;
        </span>
        <img src={imageSrc} alt={altText} style={{ maxWidth: '100%', maxHeight: '100%' }} />
      </div>
    </Modal>
  );
};

const modalStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

export default ImagePreviewModal;
