import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

const ItemDetail = ({ itemId }) => {
  const [item, setItem] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${id}`);
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItem();
  }, [itemId]);

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div id="carouselExampleDark" className="carousel carousel-dark slide" data-bs-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active" data-bs-interval="10000">
            <img className="d-block w-100" src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title} />
            <div className="carousel-caption d-none d-md-block">
              <h5>First slide label</h5>
              <p>Some representative placeholder content for the first slide.</p>
            </div>
          </div>
          {/* Additional carousel items go here */}
        </div>
        <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleDark" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleDark" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>

      {/* Thumbnails Container */}
      <div className="thumbnail-container mt-3">
        <button type="button" className="btn btn-link thumbnail" data-bs-target="#carouselExampleDark" data-bs-slide-to="0" aria-label="Slide 1">
          <img className="d-block w-100 thumbnail-img" src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title} />
        </button>
        <button type="button" className="btn btn-link thumbnail" data-bs-target="#carouselExampleDark" data-bs-slide-to="1" aria-label="Slide 2">
          <img className="d-block w-100 thumbnail-img" src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.itemPhoto1.name} />
        </button>
        <button type="button" className="btn btn-link thumbnail" data-bs-target="#carouselExampleDark" data-bs-slide-to="2" aria-label="Slide 3">
          <img className="d-block w-100 thumbnail-img" src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`} alt={item.itemPhoto2.name} />
        </button>
      </div>
    </>
  );
};

export default ItemDetail;
