import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

const AdminItemDetail = () => {
  const [item, setItem] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/allItems/getItem/${id}`);
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItem();
  }, [id]);

  if (!item) {
    return <div>Loading...</div>;
  }

  // Filter out photos with missing data
  const itemPhotos = [
    { photoType: item.itemPhoto1?.photoType, data: item.itemPhoto1?.data, name: item.itemPhoto1?.name },
    { photoType: item.itemPhoto2?.photoType, data: item.itemPhoto2?.data, name: item.itemPhoto2?.name },
    { photoType: item.itemPhoto3?.photoType, data: item.itemPhoto3?.data, name: item.itemPhoto3?.name }
  ].filter(photo => photo.data); // Filter out photos with missing data

  return (
    <>
      <div className='container border border-warning '>
        <div className='row'>
          <div className='col-lg-5 col-md-12 position-relative p-4 m-4'>
            <div id="carouselSliderOfItem" className="carousel carousel-dark slide">
              <div className="carousel-indicators mt-4 mx-2">
                {itemPhotos.map((photo, index) => (
                  <button   type="button" key={index} data-bs-target="#carouselSliderOfItem" data-bs-slide-to={index} className={index === 0 ? "active thumbnail mx-2 " : "btn thumbnail mx-2"} aria-label={`Slide ${index + 1}`}>
                    <img className="d-block w-100 border border-dark border-4  " src={`data:${photo.photoType};base64,${photo.data}`} alt={photo.name} />
                  </button>
                ))}
              </div>
              <div className="carousel-inner">
                {itemPhotos.map((photo, index) => (
                  <div key={index} className={index === 0 ? "carousel-item active" : "carousel-item"}>
                    <img className="d-block w-100" src={`data:${photo.photoType};base64,${photo.data}`} alt={photo.name} />
                  </div>
                ))}
              </div>
            </div>
          </div>
          <div className='col-lg-1'></div>
          <div className='col-lg-5 col-md-12 m-4 p-4'>
            <div>wefefwefwefwefwe</div>
          </div>
        </div>
      </div>

      <div className='container'>
        <div className='row'>
            <div className='col-5 mx-2'>
            <center><button className='btn btn-warning mb-4 p-3'onClick={handleButtonClick}>Remove</button></center>
                <Modal
                    className='bg-dark'
                    shouldCloseOnOverlayClick={true}
                    isOpen={isModalOpen}
                    shouldCloseOnEsc={true}
                    onRequestClose={closeModal}
                    contentLabel="Example Modal"
                    style={modalStyle}>
                    <div className='fs-1 text-info bg-dark text-center '>Warning</div>
                                <div className='fs-4 text-info bg-dark text-center my-3'>Are you sure to remove this user, which name is {item.fullName}?</div>

                <label> Why are you delete this :  </label>           
                <input type="text" name="nickName" value={reason} onChange={handleChange} class="form-control" required  />           

                            <div className='row bg-dark my-3'>
                            <div className='col-4'>
                            </div>
                            <div className='col-2 '>
                                <button onClick={() => { handleDelete(item.roles.toString()); closeModal(); }} disabled={!reason.trim()}>Yes</button>
                            </div>
                            <div className='col-2 '>
                                <button onClick={closeModal}>No</button>
                            </div>                    
                    </div>                 
                </Modal>
            </div>

            <div className='col-5 mx-2'>
            {item.status.toLowerCase().trim() == 'pending' ? 
            <center><button className='btn btn-warning mb-4 p-3'onClick={handleButtonClick1}>Active</button></center>  : null}
                <Modal
                    className='bg-dark'
                    shouldCloseOnOverlayClick={true}
                    isOpen={isModalOpen1}
                    shouldCloseOnEsc={true}
                    onRequestClose={closeModal1}
                    contentLabel="Example Modal"
                    style={modalStyle1}>
                    <div className='fs-1 text-info bg-dark text-center '>Alert!</div>
                                <div className='fs-4 text-info bg-dark text-center my-3'>Are you sure to Active this user, which name is {item.fullName}?</div>
                            <div className='row bg-dark my-3'>
                            <div className='col-4'>
                            </div>
                            <div className='col-2 '>
                                <button onClick={() => { handleActive(); closeModal1(); }} >Yes</button>
                            </div>
                            <div className='col-2 '>
                                <button onClick={closeModal1}>No</button>
                            </div>                    
                    </div>                 
                </Modal>
            </div>
        </div>
        </div>
  
    </>
  );
};

export default AdminItemDetail;
