import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);

  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/item/seller/7afa80cc-e278-4a38-b41a-9be258b1fe99');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const handleSearch = () => {
    // Filter items based on the search term
    const filteredItems = items.filter(item =>
      item.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setSearchResult(filteredItems);
  };

  const textStyle = {
    color: '#FF9400',
    fontWeight: 'bold'
  };

  return (
    <div className='bg-dark'>
      <h1>Item List</h1>
      <div className='container m-4 p-4'>
        <div className='row'>
          <div className='col-4'></div>
          <div className='col-4'>
            <label htmlFor="search" className='m-3' style={textStyle}>Search by name: </label>
            <input
              placeholder="Search" 
              aria-label="Search"
              type="text"
              id="search"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
            <button className="btn btn-outline-success my-2 my-sm-0" type="button" onClick={handleSearch}>
              Search
            </button>
          </div>
          <div className='col-4'></div>
        </div>
      </div>
      <ul>
        {(searchResult.length > 0 ? searchResult : items).map(item => (
          <div className='container border border-light' key={item.id}>
            <div className='row m-4'>
              <div className='col-2'></div>
              <li key={item.id}>
                <Link to={`/items/${item.id}`} className="text-light text-decoration-none">
                  {/* Rest of your item display code */}
                </Link>
              </li>
            </div>
          </div>
        ))}
      </ul>
      {searchResult.length === 0 && searchTerm !== '' && (
        <p style={textStyle}>No results found for "{searchTerm}"</p>
      )}
    </div>
  );
};

export default ItemList;
