import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import ImagePreviewModal from '../Common/ImagePreviewModal';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import Modal from 'react-modal';
import axios from 'axios';

const AdminSliderAds = () => {
  const searchParams = new URLSearchParams(useLocation().search);
  const token = searchParams.get('token');
  const adminId = searchParams.get('adminId');
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [submitEnabled, setSubmitEnabled] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      const BearerToken = token;
      try {
        const response = await fetch(`http://localhost:8080/api/v1/admin/getSliderPhoto`, {
          headers: {
            'Authorization': `Bearer ${BearerToken}`,
            'Content-Type': 'application/json',
          },
        });
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleButtonClick = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const editPost = (itemId) => {
    navigate(`/editPost?token=${token}&userId=${adminId}&itemId=${itemId}`);
  };

  const handleDelete = async (itemId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/v1/item/deleteItem/${itemId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ userId: adminId })
      });

      if (response.ok) {
        console.log('Delete operation successful');
        setTimeout(() => {
          window.location.reload();
        }, 1000);
        alert('Delete operation successful')
      } else {
        console.error('Delete operation failed');
        alert('Delete operation failed')
      }
    } catch (error) {
      console.error('Error during delete operation', error);
      alert('Error during delete operation')
    }
  };

  const handleFileInputChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setSubmitEnabled(true);
  };

  const handleRemoveImage = () => {
    setSelectedFile(null);
    setSubmitEnabled(false);
  };

  const openImagePreview = (itemId, imageSrc) => {
    setSelectedImage({ itemId, imageSrc });
  };

  const closeImagePreview = () => {
    setSelectedImage(null);
  };

  const handleSubmit = async () => {
    try {
      const formData = new FormData();
      formData.append('AdsPhoto', selectedFile);

      const response = await axios.post(`http://localhost:8080/api/v1/admin/addSliderPhoto/${adminId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`,
        }
      });

      if (response.status == 200) {
        alert("successfully added");
        window.location.reload();
      } else {
        alert("unsuccesfull!");
        window.location.reload();
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };
  const modalStyle = {
    content: {
      width: '50%', 
      top: isModalOpen ? '50%' : '-100%', 
      left: '50%',
      transform: 'translate(-50%, -50%)',
      transition: 'top 0.3s ease-in-out', 
      zIndex: 2000, 
      border: '10px solid #FFD700',
      opacity:0.75,
      fontWeight: 'bold',
      borderRadius: '10px',
      padding: '20px',
      position: 'relative',
      
    },
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0.5)', 
      transition: 'background-color 0.3s ease-in-out', 
    },
  };

  return (
    <>
      <div className='container border' style={{ height: '250px' }}>
        <div className='row'>
          <div className='d-flex col-6  fs-1 border ' style={{ height: '250px' }}>
            <label htmlFor="fileInput" className="add-slider-button justify-content-center">
              Add A Slider Image  <div className='fs-1'>Click Here...</div>
            </label>
            <input
              type="file"
              id="fileInput"
              accept="image/*"
              onChange={handleFileInputChange}
              style={{ display: 'none' }}
            />
          </div>
          <div className='col-6 border'>
            {selectedFile && (
              <div className="image-preview">
                <center>
                  <button onClick={handleRemoveImage} className="remove-button fs-4">
                    <img src={URL.createObjectURL(selectedFile)} alt="Selected" style={{ width: 240, height: 240 }} />
                    <FontAwesomeIcon icon={faTimes} />
                  </button>
                </center>
              </div>
            )}
          </div>
        </div>
        <div className='row'>
          {submitEnabled && (
            <button onClick={handleSubmit} className="submit-button my-4 button btn btn-warning" >
              Submit
            </button>
          )}
        </div>

        <ul type='none' className='my-4'>
          {items.length !== 0 ? items.map(item => (
            <div className='container-fluid border border-light m-4 view overlay zoom' key={item.id}>
              <div className='row m-4'>
                <li key={item.id}>
                  <Link to={`/items/${item.id}`} className="text-light text-decoration-none">
                    <div className='row m-4'>
                      <div className='col-lg-3 col-md-4'>
                        {item && (
                          <img
                            className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                            src={`data:${item.photoType};base64,${item.data}`}
                            style={{ width: '200px', maxHeight: '200px', cursor: 'pointer' }}
                            alt={item.title}
                            onClick={() => openImagePreview(item.id, `data:${item.photoType};base64,${item.data}`)}
                          />
                        )}
                      </div>
                    </div>
                  </Link>
                </li>
                <div className='row'>
                  <div className='col-1'>
                    <button className='btn btn-warning' onClick={() => editPost(item.id)}>Edit</button>
                  </div>
                  <div className='col-1'>
                    <button className='btn btn-warning' onClick={() => handleButtonClick(item.id)}>Delete</button>
                    <Modal
                      className='bg-dark'
                      shouldCloseOnOverlayClick={true}
                      isOpen={isModalOpen}
                      shouldCloseOnEsc={true}
                      onRequestClose={closeModal}
                      contentLabel="Example Modal"
                      style={modalStyle}>
                      <div className='fs-1 text-info bg-dark text-center '>Warning!</div>
                      <div className='fs-4 text-info bg-dark text-center my-3'>Are you sure to delete this post, which title is {item.title}?</div>
                      <label> Why are you delete this :  </label>

                      <div className='row bg-dark my-3'>
                        <div className='col-4'>
                        </div>
                        <div className='col-2 '>
                          <button onClick={() => { handleDelete(item.id); closeModal(); }}>Yes</button>
                        </div>
                        <div className='col-2 '>
                          <button onClick={closeModal}>No</button>
                        </div>
                      </div>
                    </Modal>
                  </div>
                </div>
              </div>
            </div>
          )) : <li><div className='fs-1 '><center>"Currently, no any advertisement found"</center></div></li>}
        </ul>
      </div>
    </>
  )
}

export default AdminSliderAds;
