import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    nickName: '',
    dob: '',
    gender: '',
    address: '',
    telephone: '',
    email: '',
    password: '',
    confirmPassword: '',
    otp: '',
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [resendDisabled, setResendDisabled] = useState(false);
  const [resendCountdown, setResendCountdown] = useState(120);

  useEffect(() => {
    let countdownTimer;

    if (resendCountdown > 0 && resendDisabled) {
      countdownTimer = setTimeout(() => {
        setResendCountdown((prevCountdown) => prevCountdown - 1);
      }, 1000);
    }

    return () => {
      clearTimeout(countdownTimer);
    };
  }, [resendCountdown, resendDisabled]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSendOTP = async () => {
    try {
      const response = await axios.post('http://your-backend-url/api/v1/otp/sendOTP', {
        email: formData.email,
      });
      // Handle response as needed
      setOtpSent(true);
      setResendDisabled(true);
      setResendCountdown(120);
    } catch (error) {
      // Handle error
    }
  };

  const handleResendOTP = async () => {
    try {
      const response = await axios.post('http://your-backend-url/api/v1/otp/reSendOTP', {
        email: formData.email,
      });
      // Handle response as needed
      setResendDisabled(true);
      setResendCountdown(120);
    } catch (error) {
      // Handle error
    }
  };

  const handleVerifyOTP = async () => {
    try {
      const response = await axios.post('http://your-backend-url/api/v1/otp/verifyOTP', {
        email: formData.email,
        otp: parseInt(formData.otp, 10),
      });
      // Handle response as needed
      setOtpVerified(true);
    } catch (error) {
      // Handle error
      alert('Incorrect OTP entered');
    }
  };

  const handleSubmit = async () => {
    try {
      // Add additional frontend validations as needed
      if (otpVerified) {
        const response = await axios.post('http://your-backend-url/api/v1/register', formData);
        // Handle response as needed, e.g., navigate to success page
        console.log(response.data);
      } else {
        // Show error message or handle the case where OTP is not verified
      }
    } catch (error) {
      // Handle error, e.g., show pop-up with "registration unsuccessful" message
      console.error(error);
    }
  };

  return (
    <form>
      <label>
        Full Name:
        <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} />
      </label>
      <label>
        Nick Name:
        <input type="text" name="nickName" value={formData.nickName} onChange={handleChange} />
      </label>
      <label>
        Date of Birth (YYYY-MM-DD):
        <input type="text" name="dob" value={formData.dob} onChange={handleChange} />
      </label>
      <label>
        Gender:
        <input type="text" name="gender" value={formData.gender} onChange={handleChange} />
      </label>
      <label>
        Address:
        <input type="text" name="address" value={formData.address} onChange={handleChange} />
      </label>
      <label>
        Telephone (10 digits, starts with 0):
        <input type="text" name="telephone" value={formData.telephone} onChange={handleChange} />
      </label>
      <label>
        Email:
        <input type="text" name="email" value={formData.email} onChange={handleChange} />
      </label>
      <label>
        Password:
        <input type="password" name="password" value={formData.password} onChange={handleChange} />
      </label>
      <label>
        Confirm Password:
        <input
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
        />
      </label>
      <button type="button" onClick={handleSendOTP}>
        {resendDisabled ? `Resend OTP (${resendCountdown}s)` : 'Send OTP'}
      </button>
      {otpSent && (
        <>
          <label>
            Enter OTP:
            <input type="text" name="otp" onChange={handleChange} />
          </label>
          <button type="button" onClick={handleVerifyOTP}>
            Verify OTP
          </button>
        </>
      )}
      {otpVerified && (
        <button type="button" onClick={handleSubmit}>
          Submit
        </button>
      )}
      {resendDisabled && (
        <button type="button" onClick={handleResendOTP} disabled>
          Resend OTP
        </button>
      )}
    </form>
  );
};

export default RegistrationForm;
