import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';


const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    FullName: '',
    NickName: '',
    dob: '',
    gender: '',
    address: '',
    telephone: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [showOtpModal, setShowOtpModal] = useState(false);
  const [otp, setOtp] = useState('');
  const [verificationSuccess, setVerificationSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleOtpChange = (e) => {
    setOtp(e.target.value);
  };

  const handleVerifyOtp = () => {
    // Here you can make an API call to your Spring Boot backend
    // to verify the OTP. For now, let's assume verification is successful.
    setVerificationSuccess(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation logic
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const telephoneRegex = /^0[0-9]{9}$/;

    if (!emailRegex.test(formData.email)) {
      alert('Invalid email format');
      return;
    }

    if (!telephoneRegex.test(formData.telephone)) {
      alert('Invalid telephone number format');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    // If all validations pass, proceed with OTP verification
    setShowOtpModal(true);
  };

  const handleOtpSubmit = () => {
    // Here you can make an API call to your Spring Boot backend
    // to submit the registration data. For now, let's assume successful submission.
    setShowOtpModal(false);
    alert('Registration successful!');
  };

  return (
    <div>
      <Form onSubmit={handleSubmit}>
        {/* ... (same as previous code) */}

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>

      {/* OTP Verification Modal */}
      <Modal show={showOtpModal} onHide={() => setShowOtpModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Email Verification</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            An OTP has been sent to your email address. Please enter the OTP below to verify your account.
          </p>
          <Form.Group controlId="formOtp">
            <Form.Label>Enter OTP</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={handleOtpChange}
              required
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowOtpModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleVerifyOtp}>
            Verify OTP
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Success Modal */}
      <Modal show={verificationSuccess} onHide={() => setVerificationSuccess(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Registration Successful</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Your account has been successfully registered!</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleOtpSubmit}>
            Continue
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default RegistrationForm;
