import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

const ItemDetail = () => {
  const [item, setItem] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/allItems/getItem/${id}`);
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItem();
  }, [id]);

  if (!item) {
    return <div>Loading...</div>;
  }

  // Check if photos property exists and is an array
  if (!item.photos || !Array.isArray(item.photos)) {
    return <div>No photos available for this item</div>;
  }

  return (
    <>
      <div className='container border border-warning '>
        <div className='row'>
          <div className='col-lg-5 col-md-12 position-relative p-4 m-4'>
            <div id="carouselSliderOfItem" className="carousel carousel-dark slide">
              <div className="carousel-indicators mt-4">
                {item.photos.map((photo, index) => (
                  <button key={index} type="button" data-bs-target="#carouselSliderOfItem" data-bs-slide-to={index} className={index === 0 ? "active thumbnail" : "btn btn-link thumbnail"} aria-label={`Slide ${index + 1}`}>
                    <img className="d-block w-100" src={`data:${photo.photoType};base64,${photo.data}`} alt={photo.name} />
                  </button>
                ))}
              </div>
              <div className="carousel-inner">
                {item.photos.map((photo, index) => (
                  <div key={index} className={index === 0 ? "carousel-item active" : "carousel-item"}>
                    <img className="d-block w-100" src={`data:${photo.photoType};base64,${photo.data}`} alt={photo.name} />
                  </div>
                ))}
              </div>
            </div>
          </div>
          <div className='col-lg-1'></div>
          <div className='col-lg-5 col-md-12 m-4 p-4'>
            <div>wefefwefwefwefwe</div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ItemDetail;
