import React from 'react'

const AdminPendingAds = () => {
    const location = useLocation();
    const responseData = location.state?.responseData;
    const navigate = useNavigate();
    
    const [items, setItems] = useState([]);
    const [name, setName] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResult, setSearchResult] = useState([]);
    const [showNoResultsMessage, setShowNoResultsMessage] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    
      useEffect(() => {
        // Fetch items from the backend API
        const fetchItems = async () => {
            const yourBearerToken = responseData.token;
          try {
            const response = await fetch(`http://localhost:8080/api/v1/admin/getAllItems`, {
                headers: {
                  'Authorization': `Bearer ${yourBearerToken}`,
                  'Content-Type': 'application/json',
                },
              });
            const data = await response.json();
            setItems(data);
          } catch (error) {
            console.error('Error fetching items:', error);
          }
        };
    
        fetchItems();
      }, []);
    
      useEffect(() => {
        const fetchName = async () => {
          // const BearerToken = responseData.token;
          // try {
          //   const response = await fetch(`http://localhost:8080/api/v1/admin/getAllItems`, {
          //     headers: {
          //       'Authorization': `Bearer ${BearerToken}`,
          //       'Content-Type': 'application/json',
          //     },
          //   });
    
          //   if (response.ok) {
          //     const data = await response.json();
          //     setName(data.name); 
          //   } else {
          //     console.error('Error fetching user name:', response.statusText);
          //   }
          // } catch (error) {
          //   console.error('Error during fetch:', error.message);
          // }
        };
    
        fetchName();
      }, [responseData.token, responseData.userId]);
    
    
      const generateSearchVariations = (term) => {
        const variations = [];
        const cleanedTerm = term.replace(/[^\w\s]/gi, ''); // Remove symbols
    
        // Original term
        variations.push(cleanedTerm);
    
        // Remove spaces
        variations.push(cleanedTerm.replace(/\s/g, ''));
    
        // Split by spaces and join without spaces
        variations.push(cleanedTerm.split(/\s/).join(''));
    
        return variations;
      };
    
      const handleSearch = () => {
        // Reset to the first page when a new search is performed
        setCurrentPage(1);
    
        // Generate search term variations
        const searchVariations = generateSearchVariations(searchTerm.toLowerCase());
    
        // Filter items based on the cleaned search term
        const filteredItems = items.filter(item => {
          const cleanedTitle = item.title.toLowerCase().replace(/[^\w\s]/gi, '') + item.deviceType.toLowerCase().replace(/[^\w\s]/gi, '') + item.id; // Remove symbols
          return searchVariations.some(variation => cleanedTitle.includes(variation));
        });
    
        setSearchResult(filteredItems);
    
        // Update the state to show the message only if no results are found and the search button is clicked
        setShowNoResultsMessage(filteredItems.length === 0 && searchTerm !== '');
      };
    
      const editPost = (itemId) => {
        navigate(`/editPost?token=${responseData.token}&userId=${responseData.userId}&itemId=${itemId}`);
      };
    
      const handleInputChange = (e) => {
        const newSearchTerm = e.target.value;
        setSearchTerm(newSearchTerm);
    
        // Automatic filtering as the user types
        const cleanedSearchTerm = newSearchTerm.replace(/[^\w\s]/gi, ''); // Remove symbols
        const filteredItems = items.filter(item => {
          const cleanedTitle = item.title.toLowerCase().replace(/[^\w\s]/gi, ''); // Remove symbols
          return cleanedTitle.includes(cleanedSearchTerm);
        });
    
        setSearchResult(filteredItems);
        setShowNoResultsMessage(false);
      };
    
      const textStyle = {
        color: '#FFD700',
        fontWeight: 'bold',
        userSelect: 'none',
      };
    
      // Calculate the index range for the current page
      const indexOfLastItem = currentPage * ITEMS_PER_PAGE;
      const indexOfFirstItem = indexOfLastItem - ITEMS_PER_PAGE;
      const currentItems = (searchResult.length > 0 ? searchResult : items).slice(indexOfFirstItem, indexOfLastItem);
    
      // Update page number
      const paginate = (pageNumber) => setCurrentPage(pageNumber);
    
      const [isModalOpen, setIsModalOpen] = useState(false);
    
      const handleButtonClick = () => {
        setIsModalOpen(true);
      };
    
      const closeModal = () => {
        setIsModalOpen(false);
      };
      const modalStyle = {
        content: {
          width: '50%', 
          top: isModalOpen ? '50%' : '-100%', 
          left: '50%',
          transform: 'translate(-50%, -50%)',
          transition: 'top 0.3s ease-in-out', 
          zIndex: 2000, 
          border: '10px solid #FFD700',
          opacity:0.75,
          fontWeight: 'bold',
          borderRadius: '10px',
          padding: '20px',
          position: 'relative',
          
        },
        overlay: {
          backgroundColor: 'rgba(0, 0, 0, 0.5)', 
          transition: 'background-color 0.3s ease-in-out', 
        },
      };
    
      const handleDelete = async (itemId) => {
        const token = responseData.token;
        try {
          const response = await fetch(`http://localhost:8080/api/v1/item/deleteItem/${itemId}`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`,
            },
          });
    
          if (response.ok) {
            console.log('Delete operation successful');
              setTimeout(() => {
                window.location.reload();
              }, 1000);    
              alert('Delete operation successful') 
          } else {
            console.error('Delete operation failed');
            alert('Delete operation failed')
          }
        } catch (error) {
          console.error('Error during delete operation', error);
          alert('Error during delete operation')
        }
      };
    
      return (
        <>
    
          <h1 className="no-focus-outline text-center m-4">Wellcome {name}.</h1>
          {/* <div className='container'><NavBar responseData={responseData} /></div> */}
          <div className='container m-4'>
            <div className='d-flex m-4 '>
    
              <div className='input-group mb-3 '>
                <label htmlFor="search" className='d-none d-sm-block input-group-append mx-3' style={textStyle}>Search by name: </label>
                <input
                class="form-control rounded"
                  placeholder="Search"
                  aria-label="Search"
                  type="text"
                  id="search"
                  value={searchTerm}
                  onChange={handleInputChange}
                />
                <button className="btn m-2 my-sm-0 btn-outline-secondary input-group-append mx-2" type="button" onClick={handleSearch} style={textStyle}>
                  Search
                </button>
              </div>
    
            </div>
          </div>
        <div className='m-3'>
                {showNoResultsMessage && (
            <h4 style={textStyle}>No results found for "{searchTerm}".</h4>
          )}
        </div>
    
    
          <ul type='none'>
            {currentItems.map(item => (
              <div className='container-fluid border border-light m-4 view overlay zoom' key={item.id}>
                <div className='row m-4'>
    
                  <li key={item.id}>
                    <Link to={`/items/${item.id}`} className="text-light text-decoration-none">
                      <div className='row m-4'>
    
                        <div className='col-lg-3 col-md-4'>
                          <img
                            className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                            src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                            alt={item.title}
                          />
                        </div>
                        <div className='col-lg-2 col-md-1'></div>
    
                        <div className='col-lg-3 col-md-6 mr-4'>
                          <h2 className='text-center fs-1' style={textStyle}>{item.title}</h2>
                          <div className="fs-3 fw-light mt-4">Price: Rs.{item.price}</div>
                          <div className="fs-5 fw-light mb-4">Place: {item.place}</div>
                          <p class="text-end fs-6 my-2">Post Added Date: {item.date}</p>
                          {item.lastModifyDate && (
                            <p class="text-end fs-6">Last Modification Date: {item.lastModifyDate}</p>
                        )}
                        </div>
                      </div>
                      </Link>
                      <div className='col-2'>
                      </div>               
                  </li>
                  <div className='row'>
                    <div className='col-lg-10'></div>
                    <div className='col-1'>
                    <button className='btn btn-warning' onClick={() => editPost(item.id)}>Edit</button>
                    </div>
                    <div className='col-1'>
                    <button className='btn btn-warning'onClick={handleButtonClick}>Delete</button>
                      <Modal
                      className='bg-dark'
                        shouldCloseOnOverlayClick={true}
                        isOpen={isModalOpen}
                        shouldCloseOnEsc={true}
                        onRequestClose={closeModal}
                        contentLabel="Example Modal"
                        style={modalStyle}>
                              <div className='fs-1 text-info bg-dark text-center '>Warning!</div>
                              <div className='fs-4 text-info bg-dark text-center my-3'>Are you sure to delete this post, which title is {item.title}?</div>
                            <div className='row bg-dark my-3'>
                            <div className='col-4'>
                            </div>
                            <div className='col-2 '>
                              <button onClick={() => { handleDelete(item.id); closeModal(); }}>Yes</button>
                            </div>
                            <div className='col-2 '>
                              <button onClick={closeModal}>No</button>
                            </div>                    
                        </div>                 
                      </Modal>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </ul>
    
          {/* Pagination */}
          <div className='container-fulid m-4'>
            <div className='row'>
            <div className='col-5'></div>
          <nav className='col-3'>
            <ul className="pagination">
              <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => paginate(currentPage - 1)}>
                  Previous
                </button>
              </li>
              {[...Array(Math.ceil((searchResult.length > 0 ? searchResult : items).length / ITEMS_PER_PAGE)).keys()].map((number) => (
                <li key={number + 1} className={`page-item ${currentPage === number + 1 ? 'active' : ''}`}>
                  <button className="page-link" onClick={() => paginate(number + 1)}>
                    {number + 1}
                  </button>
                </li>
              ))}
              <li className={`page-item ${currentPage === Math.ceil((searchResult.length > 0 ? searchResult : items).length / ITEMS_PER_PAGE) ? 'disabled' : ''}`}>
                <button className="page-link" onClick={() => paginate(currentPage + 1)}>
                  Next
                </button>
              </li>
            </ul>
          </nav>
          <div className='col-4'></div>
          </div>
          </div>
        </>
      );
}

export default AdminPendingAds
