import React, { useState } from 'react';
import axios from 'axios';

const AddItemForm = () => {
  const [formData, setFormData] = useState({
    title: '',
    deviceType: '',
    brand: '',
    description: '',
    price: 0,
    phoneNumber: '',
    itemCondition: '',
    place: '',
    // Add other form fields here
    photo1: null,
    photo2: null,
    photo3: null,
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const data = new FormData();
    data.append('itemDetails', JSON.stringify(formData));
    data.append('itemPhoto1', formData.photo1);
    data.append('itemPhoto2', formData.photo2);
    data.append('itemPhoto3', formData.photo3);

    try {
      await axios.post('/api/v1/item/add', data);
      // Handle success
      console.log('Item and photos saved successfully');
    } catch (error) {
      // Handle error
      console.error('Error saving item and photos:', error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Title:</label>
      <input type="text" name="title" onChange={handleInputChange} />

      <label>Device Type:</label>
      <input type="text" name="deviceType" onChange={handleInputChange} />

      <label>Brand:</label>
      <input type="text" name="brand" onChange={handleInputChange} />

      <label>Description:</label>
      <textarea name="description" onChange={handleInputChange}></textarea>

      <label>Price:</label>
      <input type="number" name="price" onChange={handleInputChange} />

      <label>Phone Number:</label>
      <input type="text" name="phoneNumber" onChange={handleInputChange} />

      <label>Item Condition:</label>
      <input type="text" name="itemCondition" onChange={handleInputChange} />

      <label>Place:</label>
      <input type="text" name="place" onChange={handleInputChange} />

      <label>Photo 1:</label>
      <input type="file" name="photo1" onChange={handleFileChange} />

      <label>Photo 2:</label>
      <input type="file" name="photo2" onChange={handleFileChange} />

      <label>Photo 3:</label>
      <input type="file" name="photo3" onChange={handleFileChange} />

      <button type="submit">Submit</button>
    </form>
  );
};

export default AddItemForm;
