import React, { useState } from 'react'
import axios from 'axios';
import validator from 'validator';
import { useNavigate } from 'react-router-dom'; 

const ForggotPassword = () => {

    const[email, setEmail] = useState('');
    const[password,setPassword] = useState('');
    const [confirmPassword,setConfirmPassword] =useState('');
    const [otpSent, setOtpSent] = useState(false);
    const [otpVerified, setOtpVerified] = useState(false);
    const [resendCooldown, setResendCooldown] = useState(0);

    const handleSubmit = (e) =>{
        console.log('Email submitted:', email);
    };

    const handleResendOTP = async () => {
        try {
          await axios.post('http://localhost:8080/api/v1/otp/reSendOTP', {
            email: formData.email,
          });
          setResendCooldown(120); // Reset the cooldown to 2 minutes
        } catch (error) {
        }
      };
    
      const handleVerifyOTP = async () => {
        try {
          const response = await axios.post('http://localhost:8080/api/v1/otp/verifyOTP', {
            email: formData.email,
            otp: parseInt(formData.otp, 10),
          });
          setOtpVerified(true);
        } catch (error) {
          alert('Incorrect OTP entered');
        }
      };
  return (
    <>
    <form onSubmit={handleSubmit}>
        <div class="mb-3">
            <div className='container m-4'>
                    <div className='row'>
                        <div className='col-lg-4'></div>
                        <div className='col-lg-4'>
                            <label for="exampleInputEmail1" class="form-label">Email address : </label>
                            <input class="form-control" type='email' placeholder='Enter your email' value={email} onChange={(e) => setEmail(e.target.value)} required /> 
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Password</label>
                            <input type="password" class="form-control" value={password} onChange={(e) => setPassword(e.target.value)} required/>
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Confirm Password</label>
                            <input type="password" class="form-control" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required/>
                        </div>
                        <div className='col-lg-4'></div>
                    </div>            
                </div>

                <div className='row'>
                <div className='col-lg-6 m-4'>
                {!otpVerified && (
                    <>
                        <button
                        type="button"
                        onClick={otpSent ? handleResendOTP : handleSendOTP}
                        disabled={resendCooldown > 0}
                        className="btn btn-warning mx-2"
                        >
                        {otpSent ? 'Resend OTP' : 'Send OTP'}
                        </button>
                        {resendCooldown > 0 && <p>Resend cooldown: {resendCooldown} seconds</p>}
                    </>
                    )}
                </div>
                </div>
            </div>
        </div>

    </form>
    
    </>
  )
}

export default ForggotPassword
