import React, { useState } from 'react';

const ItemList = () => {
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetch('/api/items')
            .then(response => response.json())
            .then(data => setItems(data));
    }, []);

    const [searchTerm, setSearchTerm] = useState('');

    const navigateToDetailPage = (itemId) => {
        // Implement navigation logic to the detail page
        console.log(`Navigate to detail page for item with ID: ${itemId}`);
    };

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredItems = items.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div>
            <div>
                <input
                    type="text"
                    placeholder="Search by name..."
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </div>
            {filteredItems.map(item => (
                <ItemCard key={item.id} item={item} navigateToDetailPage={navigateToDetailPage} />
            ))}
        </div>
    );
};

const ItemCard = ({ item, navigateToDetailPage }) => {
    return (
        <div>
            <img src={item.photoUrl} alt={item.name} />
            <h3>{item.name}</h3>
            <button onClick={() => navigateToDetailPage(item.id)}>Details</button>
        </div>
    );
};

export default ItemList;
