import React, { useState, useEffect } from 'react';
import axios from 'axios';
import validator from 'validator';

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    nickName: '',
    dob: '',
    gender: '',
    address: '',
    telephone: '',
    email: '',
    password: '',
    confirmPassword: '',
    otp: '',
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [resendCooldown, setResendCooldown] = useState(0);
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [confirmPasswordError, setConfirmPasswordError] = useState('');
  const [telephoneError, setTelephoneError] = useState('');
  const [dobError, setDobError] = useState('');
  const [fullNameError, setFullNameError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  useEffect(() => {
    setEmailError('');
  }, [formData.email]);

  const validateEmail = () => {
    if (!validator.isEmail(formData.email)) {
      setEmailError('Invalid email format');
      return false;
    } else {
      setEmailError('');
      return true;
    }
  };

  const validatePassword = () => {
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/;
    if (!passwordRegex.test(formData.password)) {
      setPasswordError('Password should be at least 8 characters long and contain at least one letter and one number.');
      return false;
    } else {
      setPasswordError('');
      return true;
    }
  };

  const validateConfirmPassword = () => {
    if (formData.password !== formData.confirmPassword) {
      setConfirmPasswordError('Passwords do not match.');
      return false;
    } else {
      setConfirmPasswordError('');
      return true;
    }
  };

  const validateTelephone = () => {
    const telephoneRegex = /^0\d{9}$/;
    if (!telephoneRegex.test(formData.telephone)) {
      setTelephoneError('Telephone should be 10 digits and start with 0.');
      return false;
    } else {
      setTelephoneError('');
      return true;
    }
  };

  const validateDob = () => {
    const dobRegex = /^\d{4}-\d{2}-\d{2}$/;
    if (!dobRegex.test(formData.dob)) {
      setDobError('Date of Birth should be in the format YYYY-MM-DD.');
      return false;
    } else {
      setDobError('');
      return true;
    }
  };

  const validateFullName = () => {
    if (!formData.fullName.trim()) {
      setFullNameError('Full Name is required.');
      return false;
    } else {
      setFullNameError('');
      return true;
    }
  };

  const handleSendOTP = async () => {
    if (
      !validateEmail() ||
      !validateFullName() ||
      !validateTelephone() ||
      !validateDob() ||
      !validatePassword() ||
      !validateConfirmPassword()
    ) {
      return;
    }

    try {
      await axios.post('http://localhost:8080/api/v1/otp/sendOTP', { email: formData.email });
      setOtpSent(true);
      setResendCooldown(120);
    } catch (error) {
      alert('Failed to send OTP. Please try again.');
    }
  };

  const handleResendOTP = async () => {
    try {
      await axios.post('http://localhost:8080/api/v1/otp/reSendOTP', {
        email: formData.email,
      });
      setResendCooldown(120); // Reset the cooldown to 2 minutes
    } catch (error) {
      // Handle error
    }
  };

  const handleVerifyOTP = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/v1/otp/verifyOTP', {
        email: formData.email,
        otp: parseInt(formData.otp, 10),
      });
      setOtpVerified(true);
    } catch (error) {
      alert('Incorrect OTP entered');
    }
  };

  const handleSubmit = async () => {
    const isPasswordValid = validatePassword();
    const isConfirmPasswordValid = validateConfirmPassword();

    if (otpVerified && isPasswordValid && isConfirmPasswordValid) {
      try {
        const response = await axios.post('http://localhost:8080/api/v1/register', formData);
        console.log(response.data);
      } catch (error) {
        console.error(error);
      }
    }
  };

  useEffect(() => {
    let interval;
    if (resendCooldown > 0) {
      interval = setInterval(() => {
        setResendCooldown((prev) => Math.max(prev - 1, 0));
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [resendCooldown]);

  return (
    <form>
        <div class="form-row container">

            <div class="col-md-4 mb-3">
                    <label for="inputFullName">Full Name:</label>                   
                    <input required type="text" name="fullName" value={formData.fullName} onChange={handleChange} onBlur={validateFullName} class="form-control" />
                    <div class="invalid-feedback">
                        Please provide a valid city.
                    </div>               
            </div>

            <div class="col-md-4 mb-3">
                <label> Nick Name:  </label>           
                <input type="text" name="nickName" value={formData.nickName} onChange={handleChange} class="form-control" />           
            </div> 
            <div class="col-md-4 mb-3">
                <label>Date of Birth (YYYY-MM-DD):  </label>
                <input type="text" name="dob" value={formData.dob} onChange={handleChange} onBlur={validateDob} />
                <div style={{ color: 'red' }}>{dobError}</div>        
            </div>

            <fieldset class="form-group">
                <div class="row">
                <legend class="col-form-label col-sm-2 pt-0"> Gender:</legend>
                <div class="col-sm-10">
                    <div class="form-check">
                    <input class="form-check-input" type="radio" name="gender" id="gridRadios1" value={formData.gender} onChange={handleChange} checked />
                    <label class="form-check-label" for="gridRadios1">
                       Male
                    </label>
                    </div>
                    <div class="form-check">
                    <input class="form-check-input" type="radio" name="gender" id="gridRadios2" value={formData.gender} onChange={handleChange} checked />
                    <label class="form-check-label" for="gridRadios2">
                        Female
                    </label>
                    </div>
                </div>
                </div>
            </fieldset>
    
            <div class="col-md-4 mb-3">      
                <label>Address:</label>
                <input type="text" name="address" value={formData.address} onChange={handleChange} />
            </div>

            <div class="col-md-4 mb-3"> 
                <label>Telephone (10 digits, starts with 0):</label>
                <input type="text" name="telephone" value={formData.telephone} onChange={handleChange} onBlur={validateTelephone} />
                <div style={{ color: 'red' }}>{telephoneError}</div>
            </div>
      
            <div class="col-md-4 mb-3"> 
                <label> Email:  </label>
            <input type="text" name="email" value={formData.email} onChange={handleChange} onBlur={validateEmail} />
            <div style={{ color: 'red' }}>{emailError}</div>
            </div>
            <div class="col-md-4 mb-3"> 
                <label>
                    Password:
                    <input
                    type="password"
                    name="password"
                    required
                    value={formData.password}
                    onChange={handleChange}
                    onBlur={validatePassword}
                    />
                    <div style={{ color: 'red' }}>{passwordError}</div>
                </label>
            </div>
            <div class="col-md-4 mb-3"> 
                <label>
                    Confirm Password:
                    <input
                    type="password"
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    onBlur={validateConfirmPassword}
                    />
                    <div style={{ color: 'red' }}>{confirmPasswordError}</div>
                </label>
            </div>    
      <button type="button" onClick={otpSent ? handleResendOTP : handleSendOTP} disabled={resendCooldown > 0}>
        {otpSent ? 'Resend OTP' : 'Send OTP'}
      </button>
      {otpSent && (
        <>
          <label>
            Enter OTP:
            <input type="text" name="otp" onChange={handleChange} />
          </label>
          <button type="button" onClick={handleVerifyOTP}>
            Verify OTP
          </button>
        </>
      )}
      {otpVerified && (
        <button type="button" onClick={handleSubmit}>
          Submit
        </button>
      )}
      {resendCooldown > 0 && <p>Resend cooldown: {resendCooldown} seconds</p>}
      </div>
    </form>
  );
};

export default RegistrationForm;
