import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
// import  styles from '../CSS/ItemDetails.module.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

const ItemDetail = ({itemId}) => {
    const [item, setItem] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchItem = async () => {
            try {
              const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${id}`);
              const data = await response.json();
              setItem(data);
            } catch (error) {
              console.error('Error fetching items:', error);
            }
          };
                fetchItem();
    }, [itemId]);

    if (!item) {
        return <div>Loading...</div>;
    }
    

    return (
        <>


    <div id="carouselExampleDark" class="carousel carousel-dark slide" data-bs-ride="carousel">
        <div class="carousel-indicators">
            <button type="button" data-bs-target="#carouselExampleDark" data-bs-slide-to="0" class="active thumbnail" aria-current="true" aria-label="Slide 1">
                <img class="d-block w-100"  src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title}/>
            </button>
            <button type="button" class="btn btn-link thumbnail"  data-bs-target="#carouselExampleDark" data-bs-slide-to="1" aria-label="Slide 2">
            <img class="d-block w-100"  src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.itemPhoto1.name}/>
            </button>
            <button type="button" class="btn btn-link thumbnail" data-bs-target="#carouselExampleDark " data-bs-slide-to="2" aria-label="Slide 3">
            <img class="d-block w-100"  src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`} alt={item.itemPhoto2.name}/>
            </button>
        </div>
        <div class="carousel-inner">
            <div class="carousel-item active" >
            <img class="d-block w-100"  src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title}/>
            <div class="carousel-caption d-none d-md-block">
                <h5>First slide label</h5>
                <p>Some representative placeholder content for the first slide.</p>
            </div>
            </div>
            <div class="carousel-item" >
            <img class="d-block w-100"  src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.itemPhoto1.name}/>
            <div class="carousel-caption d-none d-md-block">
                <h5>Second slide label</h5>
                <p>Some representative placeholder content for the second slide.</p>
            </div>
            </div>
            <div class="carousel-item">
            <img class="d-block w-100"  src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`} alt={item.itemPhoto2.name}/>
            <div class="carousel-caption d-none d-md-block">
                <h5>Third slide label</h5>
                <p>Some representative placeholder content for the third slide.</p>
            </div>
            </div>
        </div>
    </div>

        
</>
    );
};

export default ItemDetail;
