import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import  styles from '../CSS/ItemDetails.module.css';
const ItemDetail = ({itemId}) => {

    const [item, setItem] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchItem = async () => {
            try {
              const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${id}`);
              const data = await response.json();
              setItem(data);
            } catch (error) {
              console.error('Error fetching items:', error);
            }
          };
                fetchItem();
    }, [itemId]);

    if (!item) {
        return <div>Loading...</div>;
    }

    const [activeIndex, setActiveIndex] = useState(0);

    const handleSelect = (selectedIndex) => {
      setActiveIndex(selectedIndex);
    };
  
    const renderThumbnails = () => {

      const thumbnailData = [
        <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}alt={item.title}/>,
        <img src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`}alt={item.title}/>,
        <img src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`}alt={item.title}/>,

      ];

      return thumbnailData.map((thumbnail, index) => (
        <li
          key={index}
          data-target="#carouselExample" 
          data-slide-to={index}
          className={index === activeIndex ? 'active' : ''}
        >
          <img src={thumbnail} alt={`Thumbnail ${index}`} className="img-fluid" />
        </li>
      ));
    };
  
    const renderSlides = () => {
      const slideData = [
        <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}alt={item.title}/>,
        <img src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`}alt={item.title}/>,
        <img src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`}alt={item.title}/>,
      ];
  
      return slideData.map((slide, index) => (
        <div key={index} className={`carousel-item ${index === activeIndex ? 'active' : ''}`}>
          <img src={slide} alt={`Slide ${index}`} className="d-block w-100" />
        </div>
      ));
    };

    return (
        <>
         <div id="carouselExample" className="carousel slide" data-ride="carousel">
            <ol className="carousel-indicators">
                {renderThumbnails()}
            </ol>
            <div className="carousel-inner">
                {renderSlides()}
            </div>
            <a className="carousel-control-prev" href="#carouselExample" role="button" data-slide="prev" onClick={() => handleSelect((activeIndex - 1 + renderSlides().length) % renderSlides().length)}>
                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                <span className="sr-only">Previous</span>
            </a>
            <a className="carousel-control-next" href="#carouselExample" role="button" data-slide="next" onClick={() => handleSelect((activeIndex + 1) % renderSlides().length)}>
                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                <span className="sr-only">Next</span>
            </a>
        </div>
        
        <div className={styles.container} >
                <div className="box">
                    <div className="product__img img-fluid img-thumbnail">
                    <img class="no-border img-fluid img-thumbnail " src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title} />
                    </div>
        
                    <div className="product__video">
                        <div className="video">
                            <i className="fas fa-play"></i>
                            <p>watch a review</p>
                        </div>
                    </div>
        
                    <div className="product__disc">
                        <div className="product__disc--content">
                            <div className="disc__content--about">
                                <h1>Oculus Rift S</h1>
                                <span>get ready to step into a new world</span>
                                <p>{item.description}</p>
                            </div>
        
                            <div className="product__view--more">
                                <p>view all specifications</p>
                                <div className="view__more--block"></div>
                            </div>
                        </div>
                    </div>
        
                    <div className="product_buttons">
                        <button className="btn wishlist">wishlist</button>
                        <button className="btn buy">buy</button>
                    </div>
                </div>
            </div>
        
        
        
        <a href="https://youtu.be/NBIbZIegMRY" target="_blank"><footer>
            <div className="texto">
                <span>
                    <i className="fab fa-youtube"></i>
                     watch on youtube</span>
            </div>
        </footer>
           </a>
        </>
    );
};

export default ItemDetail;
