import React, { useState } from 'react';
import Resizer from 'react-image-file-resizer';

const ItemForm = () => {
  const [itemDetails, setItemDetails] = useState({
    itemName: '',
    place: '',
    itemType: '',
    itemPhoto: null, // Updated to store the file object
    condition: '',
    description: '',
    contactNumber: '',
    brand: '',
    model: '',
    price: '',
  });

  const handleChange = (e) => {
    const { name, value, type } = e.target;

    // If the input type is file, handle the file separately
    if (type === 'file') {
      const file = e.target.files[0];

      // Resize the image to 400x400 pixels using react-image-file-resizer
      Resizer.imageFileResizer(
        file,
        400, // New width
        400, // New height
        'JPEG', // Compression format
        100, // Compression quality
        0, // Rotation angle
        (uri) => {
          // The resized image uri is passed to the callback
          setItemDetails((prevDetails) => ({ ...prevDetails, [name]: uri }));
        },
        'base64' // Output type
      );
    } else {
      setItemDetails((prevDetails) => ({ ...prevDetails, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();

      // Append form data, including the resized image
      Object.entries(itemDetails).forEach(([key, value]) => {
        formData.append(key, value);
      });

      const response = await fetch('http://your-api-endpoint/items', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        console.log('Item added successfully');
      } else {
        console.error('Failed to add item');
      }
    } catch (error) {
      console.error('Error submitting item:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* ... Other input fields ... */}
      
      <label>Item Photo:</label>
      <input type="file" name="itemPhoto" onChange={handleChange} accept="image/*" />

      {itemDetails.itemPhoto && (
        <img
          src={itemDetails.itemPhoto}
          alt="Item Preview"
          style={{ maxWidth: '400px', maxHeight: '400px' }}
        />
      )}

      <label>Item Name:</label>
      <input type="text" name="itemName" value={itemDetails.itemName} onChange={handleChange} />

      <label>Place:</label>
      <input type="text" name="place" value={itemDetails.place} onChange={handleChange} />

      <label>Item Type:</label>
      <input type="text" name="itemType" value={itemDetails.itemType} onChange={handleChange} />

      <label>Condition:</label>
      <input type="text" name="condition" value={itemDetails.condition} onChange={handleChange} />

      <label>Description:</label>
      <textarea name="description" value={itemDetails.description} onChange={handleChange}></textarea>

      <label>Contact Number:</label>
      <input type="text" name="contactNumber" value={itemDetails.contactNumber} onChange={handleChange} />

      <label>Brand:</label>
      <input type="text" name="brand" value={itemDetails.brand} onChange={handleChange} />

      <label>Model:</label>
      <input type="text" name="model" value={itemDetails.model} onChange={handleChange} />

      <label>Price:</label>
      <input type="text" name="price" value={itemDetails.price} onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
};

export default ItemForm;
