import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../CSS/DartTheme.css';
import NavBar from './NavBar';
import Slider from './adsSlider';

const ITEMS_PER_PAGE = 5;

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [showNoResultsMessage, setShowNoResultsMessage] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [filter, setFilter] = useState('all'); 

  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/allItems/getAllItems');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const handleFilterChange = (event) => {
    const selectedFilter = event.target.value;
    setFilter(selectedFilter);
};

// Filter items based on the selected filter
const filteredItems = items.filter(item => {
  if (filter === 'laptop') {
    return item.deviceType === 'laptop';
  } else if (filter === 'desktop') {
    return item.deviceType === 'desktop';
  } else if (filter === 'monitor') {
    return item.deviceType === 'monitor';
  } else if (filter === 'motherboard') {
    return item.deviceType === 'motherboard';
  } else if (filter === 'graphics card') {
    return item.deviceType === 'graphics card';
  } else if (filter === 'ram') {
    return item.deviceType === 'ram';
  } else if (filter === 'cpu cooler') {
    return item.deviceType === 'cpu cooler';
  } else if (filter === 'casing') {
    return item.deviceType === 'casing';
  } else if (filter === 'argb fans') {
    return item.deviceType === 'argb fans';
  } else if (filter === 'power supply') {
    return item.deviceType === 'power supply';
  } else if (filter === 'ups') {
    return item.deviceType === 'ups';
  } else if (filter === 'keyboard') {
    return item.deviceType === 'keyboard';
  } else if (filter === 'mouse') {
    return item.deviceType === 'mouse';
  } else if (filter === 'other accessories') {
    return item.deviceType === 'other accessories';
  } else if (filter === 'hard disk') {
    return item.deviceType === 'hard disk';
  } else {
    return true;
  }
});

  const generateSearchVariations = (term) => {
    const variations = [];
    const cleanedTerm = term.replace(/[^\w\s]/gi, ''); // Remove symbols

    // Original term
    variations.push(cleanedTerm);

    // Remove spaces
    variations.push(cleanedTerm.replace(/\s/g, ''));

    // Split by spaces and join without spaces
    variations.push(cleanedTerm.split(/\s/).join(''));

    return variations;
  };

  const handleSearch = () => {
    // Reset to the first page when a new search is performed
    setCurrentPage(1);

    // Generate search term variations
    const searchVariations = generateSearchVariations(searchTerm.toLowerCase());

    // Filter items based on the cleaned search term
    const filteredItems = items.filter(item => {
      const cleanedTitle = item.title.toLowerCase().replace(/[^\w\s]/gi, ''); // Remove symbols
      const matchesSearch = searchVariations.some(variation => cleanedTitle.includes(variation));

      if (filter === 'laptop') {
        return item.deviceType === 'laptop' && matchesSearch;
      } else if (filter === 'desktop') {
        return item.deviceType === 'desktop' && matchesSearch;
      } else if (filter === 'monitor') {
        return item.deviceType === 'monitor' && matchesSearch;
      } else if (filter === 'motherboard') {
        return item.deviceType === 'motherboard' && matchesSearch;
      } else if (filter === 'graphics card') {
        return item.deviceType === 'graphics card' && matchesSearch;
      } else if (filter === 'ram') {
        return item.deviceType === 'ram' && matchesSearch;
      } else if (filter === 'cpu cooler') {
        return item.deviceType === 'cpu cooler' && matchesSearch;
      } else if (filter === 'casing') {
        return item.deviceType === 'casing' && matchesSearch;
      } else if (filter === 'argb fans') {
        return item.deviceType === 'argb fans' && matchesSearch;
      } else if (filter === 'power supply') {
        return item.deviceType === 'power supply' && matchesSearch;
      } else if (filter === 'ups') {
        return item.deviceType === 'ups' && matchesSearch;
      } else if (filter === 'keyboard') {
        return item.deviceType === 'keyboard' && matchesSearch;
      } else if (filter === 'mouse') {
        return item.deviceType === 'mouse' && matchesSearch;
      } else if (filter === 'other accessories') {
        return item.deviceType === 'other accessories' && matchesSearch;
      }else if (filter === 'hard disk') {
        return item.deviceType === 'hard disk' && matchesSearch;
      } else {
        return matchesSearch; // 'all' filter, return all items that match the search query
      }
    });

    setSearchResult(filteredItems);

    // Update the state to show the message only if no results are found and the search button is clicked
    setShowNoResultsMessage(filteredItems.length === 0 && searchTerm !== '');
  };

  const handleInputChange = (e) => {
    const newSearchTerm = e.target.value;
    setSearchTerm(newSearchTerm);

    // Automatic filtering as the user types
    const cleanedSearchTerm = newSearchTerm.replace(/[^\w\s]/gi, ''); // Remove symbols
    const filteredItems = items.filter(item => {
      const cleanedTitle = item.title.toLowerCase().replace(/[^\w\s]/gi, '')+ item.deviceType.toLowerCase().replace(/[^\w\s]/gi, ''); // Remove symbols
      const matchesSearch = ( cleanedTitle.includes(cleanedSearchTerm));

      if (filter === 'laptop') {
        return item.deviceType === 'laptop' && matchesSearch;
      } else if (filter === 'desktop') {
        return item.deviceType === 'desktop' && matchesSearch;
      } else if (filter === 'monitor') {
        return item.deviceType === 'monitor' && matchesSearch;
      } else if (filter === 'motherboard') {
        return item.deviceType === 'motherboard' && matchesSearch;
      } else if (filter === 'graphics card') {
        return item.deviceType === 'graphics card' && matchesSearch;
      } else if (filter === 'ram') {
        return item.deviceType === 'ram' && matchesSearch;
      } else if (filter === 'cpu cooler') {
        return item.deviceType === 'cpu cooler' && matchesSearch;
      } else if (filter === 'casing') {
        return item.deviceType === 'casing' && matchesSearch;
      } else if (filter === 'argb fans') {
        return item.deviceType === 'argb fans' && matchesSearch;
      } else if (filter === 'power supply') {
        return item.deviceType === 'power supply' && matchesSearch;
      } else if (filter === 'ups') {
        return item.deviceType === 'ups' && matchesSearch;
      } else if (filter === 'keyboard') {
        return item.deviceType === 'keyboard' && matchesSearch;
      } else if (filter === 'mouse') {
        return item.deviceType === 'mouse' && matchesSearch;
      } else if (filter === 'other accessories') {
        return item.deviceType === 'other accessories' && matchesSearch;
      }else if (filter === 'hard disk') {
        return item.deviceType === 'hard disk' && matchesSearch;
      } else {
        return matchesSearch; 
      }
      });

    setSearchResult(filteredItems);
    setShowNoResultsMessage(false);
  };

  const textStyle = {
    color: '#FFD700',
    fontWeight: 'bold',
    userSelect: 'none', // Add this line to disable text selection
  };

  // Calculate the index range for the current page
  const indexOfLastItem = currentPage * ITEMS_PER_PAGE;
  const indexOfFirstItem = indexOfLastItem - ITEMS_PER_PAGE;
  const currentItems = (searchResult.length > 0 ? searchResult : filteredItems).slice(indexOfFirstItem, indexOfLastItem);

  // Update page number
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <>
      <h1 className="no-focus-outline">Item List</h1>
      <div className='container'><NavBar /></div>
      {/* <div className='container'><Slider /></div> */}

      <div className='container m-4'>
        <div className='d-flex m-4 '>

          <div className='input-group mb-3 '>
            <label htmlFor="search" className='d-none d-sm-block input-group-append mx-3' style={textStyle}>Search by name: </label>
            <input
            class="form-control rounded"
              placeholder="Search"
              aria-label="Search"
              type="text"
              id="search"
              value={searchTerm}
              onChange={handleInputChange}
            />
            <button className="btn m-2 my-sm-0 btn-outline-secondary input-group-append mx-2" type="button" onClick={handleSearch} style={textStyle}>
              Search
            </button>
            <div className="mx-4">
            <select className="form-select" value={filter} onChange={handleFilterChange}>
                <option value="all">All Items</option>
                <option value="laptop">Laptop</option>
                <option value="desktop">Desktop</option>
                <option value="monitor">Monitor</option>
                <option value="motherboard">Motherboard</option>
                <option value="graphics card">Graphics Card</option>
                <option value="ram">RAM</option>
                <option value="cpu cooler">CPU Cooler</option>
                <option value="hard disk">Hard Disk</option>
                <option value="casing">Casing</option>
                <option value="argb fans">ARGB Fans</option>
                <option value="power supply">Power Supply</option>
                <option value="ups">UPS</option>
                <option value="keyboard">Keyboard</option>
                <option value="mouse">Mouse</option>
                <option value="other accessories">Other Accessories</option>

              </select>
            </div>
          </div>

        </div>
      </div>
    <div className='m-3'>
            {showNoResultsMessage && (
        <h4 style={textStyle}>No results found for "{searchTerm}".</h4>
      )}
    </div>

      
        <div className='container-fluid border border-light m-4 view overlay zoom rounded'>
          <div className='row m-4'>
        {currentItems.map(item => (
                      
              <div key={item.id}>
                <div className='row m-4'>
                  <div className='col-6'>
                  <Link to={`/items/${item.id}`} className="text-light text-decoration-none">                 
                    <div className='col-lg-3 col-md-4'>
                      <img
                        className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                        src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                        alt={item.title}
                      />
                    </div>

                    <div className='col-lg-3 col-md-6 mr-4'>
                      <h2 className='text-center fs-1' style={textStyle}>{item.title}</h2>
                      <div className="fs-3 fw-light mt-4">Price: Rs.{item.price}</div>
                      <div className="fs-5 fw-light mb-4">Place: {item.place}</div>
                      <p class="text-end fs-6">Post added date: {item.date}</p>
                      {item.lastModifyDate && (
                        <p class="text-end fs-6">Last Modification Date: {item.lastModifyDate}</p>
                    )}
                    </div>                  
                  </Link></div>
                  </div>
              </div>
                    
        ))}</div> </div>
      

      {/* Pagination */}
      <div className='container-fulid m-4'>
        <div className='row'>
        <div className='col-5'></div>
      <nav className='col-3'>
        <ul className="pagination">
          <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
            <button className="page-link" onClick={() => paginate(currentPage - 1)}>
              Previous
            </button>
          </li>
          {[...Array(Math.ceil((searchResult.length > 0 ? searchResult : items).length / ITEMS_PER_PAGE)).keys()].map((number) => (
            <li key={number + 1} className={`page-item ${currentPage === number + 1 ? 'active' : ''}`}>
              <button className="page-link" onClick={() => paginate(number + 1)}>
                {number + 1}
              </button>
            </li>
          ))}
          <li className={`page-item ${currentPage === Math.ceil((searchResult.length > 0 ? searchResult : items).length / ITEMS_PER_PAGE) ? 'disabled' : ''}`}>
            <button className="page-link" onClick={() => paginate(currentPage + 1)}>
              Next
            </button>
          </li>
        </ul>
      </nav>
      <div className='col-4'></div>
      </div>
      </div>
    </>
  );
};

export default ItemList;
