import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const AdminItemApprove = () => {
  const { id, token, userId } = useParams();
  const [item, setItem] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/allItems/getItem/${id}`);
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItem();
  }, [id]);

  const handleImageClick = (photo) => {
    setSelectedImage(photo);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div>Title: {item.title || 'No title available'}</div>
      <div>Model: {item.model || 'No model available'}</div>
      <div>Device Type: {item.deviceType || 'No device type available'}</div>
      <div>Item Condition: {item.itemCondition || 'No condition available'}</div>
      <div>Brand: {item.brand || 'No brand available'}</div>
      <div>Price: {item.price || 'No price available'}</div>
      <div>Phone Number: {item.phoneNumber || 'No phone number available'}</div>
      <div>Place: {item.place || 'No place available'}</div>
      <div>Description: {item.description || 'No description available'}</div>
      <div>Post add date: {item.date || 'No date available'}</div>
      <div className='container border border-danger rounded m-4'>
        <div className='row'>
          <div className='row'>
            <div className='fs-1 text-center text-danger'>
              Seller Added Item's Photos
            </div>
          </div>
          <div className='col-lg-3 m-4' onClick={() => handleImageClick(item.itemPhoto1)}>
            <div className='fs-5'>Photo 1</div>
            {item && item.itemPhoto1 && (
              <img
                className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                style={{ width: '200px', maxHeight: '200px' }}
                alt={item.title}
              />
            )}
          </div>
          <div className='col-lg-3 m-4' onClick={() => handleImageClick(item.itemPhoto2)}>
            <div className='fs-5'>Photo 2</div>
            {item && item.itemPhoto2 && (
              <img
                className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`}
                style={{ width: '200px', maxHeight: '200px' }}
                alt={item.title}
              />
            )}
          </div>
          <div className='col-lg-3 m-4' onClick={() => handleImageClick(item.itemPhoto3)}>
            <div className='fs-5'>Photo 3</div>
            {item && item.itemPhoto3 && (
              <img
                className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`}
                style={{ width: '200px', maxHeight: '200px' }}
                alt={item.title}
              />
            )}
          </div>
        </div>
      </div>

      {selectedImage && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content">
            <img
              src={`data:${selectedImage.photoType};base64,${selectedImage.data}`}
              alt={item.title}
            />
          </div>
        </div>
      )}
    </>
  );
};

export default AdminItemApprove;
