// ImagePreviewModal.js
import React from 'react';
import Modal from 'react-modal';

const ImagePreviewModal = ({ isOpen, onClose, imageSrc, altText }) => {
  return (
    <Modal isOpen={isOpen} onRequestClose={onClose} style={modalStyles}>
      <div style={{ position: 'static' }}>
        <span
          style={{
            position: 'relative',
            top: '10px',
            right: '10px',
            cursor: 'pointer',
            fontSize: '20px',
            color: 'white', // Adjust the color as needed
          }}
          onClick={onClose}
        >
          &times;
        </span>
        <img src={imageSrc} alt={altText} style={{ maxWidth: '8%', maxHeight: '100%' }} />
      </div>
    </Modal>
  );
};

const modalStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
    backgroundColor: 'transparent', // Set modal background to transparent
    border: 'none', // Remove border
    boxShadow: 'none', // Remove box shadow
  },
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.7)', // Set overlay background with some transparency
  },
};

export default ImagePreviewModal;
