import React, { useEffect, useState } from 'react';

import '../CSS/ItemDetails.css';

const ItemDetail = () => {
    const { itemId } = useParams();
    const [item, setItem] = useState(null);
  
    useEffect(() => {
      // Fetch item details from the backend API based on itemId
      const fetchItemDetails = async () => {
        try {
          const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${itemId}`);
          const data = await response.json();
          setItem(data);
        } catch (error) {
          console.error('Error fetching item details:', error);
        }
      };
  
      fetchItemDetails();
    }, [itemId]);
  
    if (!item) {
      return <div>Loading...</div>;
    }

    return (
        <>
<div class="container">
        <div class="box">
            <div class="product__img">
            <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title} />
            </div>

            <div class="product__video">
                <div class="video">
                    <i class="fas fa-play"></i>
                    <p>watch a review</p>
                </div>
            </div>

            <div class="product__disc">
                <div class="product__disc--content">
                    <div class="disc__content--about">
                        <h1>Oculus Rift S</h1>
                        <span>get ready to step into a new world</span>
                        <p>{item.description}</p>
                    </div>

                    <div class="product__view--more">
                        <p>view all specifications</p>
                        <div class="view__more--block"></div>
                    </div>
                </div>
            </div>

            <div class="product_buttons">
                <button class="btn wishlist">wishlist</button>
                <button class="btn buy">buy</button>
            </div>
        </div>
    </div>



<a href="https://youtu.be/NBIbZIegMRY" target="_blank"><footer>
    <div class="texto">
        <span>
            <i class="fab fa-youtube"></i>
             watch on youtube</span>
    </div>
</footer>
   </a>
</>
    );
};

export default ItemDetail;
