import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import ReactImageMagnify from 'react-image-magnify';

const ItemDetail = ({ itemId }) => {
  const [item, setItem] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${id}`);
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItem();
  }, [itemId]);

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div className='container border '>
        <div className='row'>
          <div className='col-lg-5 col-md-12 position-relative p-4 m-4'>
            <div id="carouselSliderOfItem" className="carousel carousel-dark slide">
              <div className="carousel-indicators mt-4">
                <button type="button" data-bs-target="#carouselSliderOfItem" data-bs-slide-to="0" className="active thumbnail" aria-current="true" aria-label="Slide 1">
                  <ReactImageMagnify
                    {...{
                      smallImage: {
                        alt: 'Slide 1',
                        isFluidWidth: true,
                        src: `data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`,
                      },
                      largeImage: {
                        src: `data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`,
                        width: 1200,
                        height: 1800,
                      },
                      enlargedImageContainerDimensions: {
                        width: 400,
                        height: 600,
                      },
                      shouldUsePositiveSpaceLens: true,
                    }}
                  />
                </button>
                <button type="button" className="btn btn-link thumbnail" data-bs-target="#carouselSliderOfItem" data-bs-slide-to="1" aria-label="Slide 2">
                  <ReactImageMagnify
                    {...{
                      smallImage: {
                        alt: 'Slide 2',
                        isFluidWidth: true,
                        src: `data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`,
                      },
                      largeImage: {
                        src: `data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`,
                        width: 1200,
                        height: 1800,
                      },
                      enlargedImageContainerDimensions: {
                        width: 400,
                        height: 600,
                      },
                      shouldUsePositiveSpaceLens: true,
                    }}
                  />
                </button>
                <button type="button" className="btn btn-link thumbnail" data-bs-target="#carouselSliderOfItem " data-bs-slide-to="2" aria-label="Slide 3">
                  <ReactImageMagnify
                    {...{
                      smallImage: {
                        alt: 'Slide 3',
                        isFluidWidth: true,
                        src: `data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`,
                      },
                      largeImage: {
                        src: `data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`,
                        width: 1200,
                        height: 1800,
                      },
                      enlargedImageContainerDimensions: {
                        width: 400,
                        height: 600,
                      },
                      shouldUsePositiveSpaceLens: true,
                    }}
                  />
                </button>
              </div>
              <div className="carousel-inner">
                <div className="carousel-item active">
                  <img className="d-block w-100" src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title} />
                </div>
                <div className="carousel-item">
                  <img className="d-block w-100" src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.itemPhoto1.name} />
                </div>
                <div className="carousel-item">
                  <img className="d-block w-100" src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`} alt={item.itemPhoto2.name} />
                </div>
              </div>
            </div>
          </div>
          <div className='col-lg-1'></div>
          <div className='col-lg-5 col-md-12 m-4 p-4'>
            <div>wefefwefwefwefwe</div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ItemDetail;
