// RegistrationForm.jsx

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const RegistrationForm = () => {
  const navigate = useNavigate();

  // Form state
  const [formData, setFormData] = useState({
    fullName: '',
    nickName: '',
    dob: '',
    gender: '',
    address: '',
    telephone: '',
    email: '',
    password: '',
    confirmPassword: '',
    otp: '',
    otpEmail: '', // new field to store email for OTP verification
  });

  const [isOtpSent, setIsOtpSent] = useState(false);
  const [otpCountdown, setOtpCountdown] = useState(120); // 2 minutes

  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    let timer;
    if (isOtpSent && otpCountdown > 0) {
      timer = setTimeout(() => {
        setOtpCountdown((prevCountdown) => prevCountdown - 1);
      }, 1000);
    } else if (otpCountdown === 0) {
      // Handle expiration or resend OTP logic here
      setErrorMessage('OTP expired. Resend if needed.');
    }
    return () => clearTimeout(timer);
  }, [isOtpSent, otpCountdown]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Additional form validation logic here

    // If the form is valid, send OTP
    sendOtp();
  };

  // Send OTP logic
  const sendOtp = async () => {
    try {
      // Send a request to the backend to generate and send OTP
      // You need to implement this API endpoint in your Spring Boot backend
      const response = await fetch('/api/send-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: formData.email }),
      });

      const data = await response.json();

      if (response.ok) {
        setIsOtpSent(true);
      } else {
        // Handle error, show pop-up, etc.
        setErrorMessage(data.message);
      }
    } catch (error) {
      console.error('Error sending OTP:', error);
    }
  };

  // Handle OTP verification
  const handleOtpVerification = async () => {
    try {
      // Check if the email in the form matches the email used for OTP verification
      if (formData.email !== formData.otpEmail) {
        // Show an error or handle the mismatch as needed
        setErrorMessage('Email mismatch for OTP verification');
        return;
      }

      // Send a request to the backend to verify OTP
      // You need to implement this API endpoint in your Spring Boot backend
      const response = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          otp: formData.otp,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // Registration successful, navigate to success page
        navigate('/success');
      } else {
        // Handle OTP verification failure, show pop-up, etc.
        setErrorMessage(data.message);
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
    }
  };

  return (
    <div>
      <h1>Registration Form</h1>
      <form onSubmit={isOtpSent ? handleOtpVerification : handleSubmit}>
        <label>
          Full Name
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Nickname
          <input
            type="text"
            name="nickName"
            value={formData.nickName}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Date of Birth (YYYY-MM-DD)
          <input
            type="text"
            name="dob"
            value={formData.dob}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Gender
          <input
            type="text"
            name="gender"
            value={formData.gender}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Address
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Telephone (10 digits, starts with 0)
          <input
            type="tel"
            name="telephone"
            pattern="[0-9]{10}"
            value={formData.telephone}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Email
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Password
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Confirm Password
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          {isOtpSent ? 'Enter OTP' : 'Email'}
          <input
            type={isOtpSent ? 'text' : 'email'}
            name={isOtpSent ? 'otp' : 'otpEmail'}
            value={isOtpSent ? formData.otp : formData.otpEmail}
            onChange={handleInputChange}
            required
          />
        </label>

        {isOtpSent && (
          <p>
            Resend OTP in {Math.floor(otpCountdown / 60)}:{otpCountdown % 60} seconds
          </p>
        )}

        {errorMessage && (
          <div style={{ color: 'red', margin: '10px 0' }}>
            {errorMessage}
          </div>
        )}

        <button type="submit">
          {isOtpSent ? 'Verify OTP' : 'Send OTP'}
        </button>
      </form>
    </div>
  );
};

export default RegistrationForm;
