useEffect(() => {
    console.log('Fetching item details for itemId:', itemId);

    const fetchItemDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${itemId}`);
        console.log('API Response:', response);

        if (response.ok) {
          const data = await response.json();
          console.log('Fetched item details:', data);
          setItem(data);
        } else {
          console.error('Error fetching item details:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching item details:', error);
      }
    };

    fetchItemDetails();
  }, [itemId]);
