import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import styles from '../CSS/ItemDetails.module.css';

const ItemDetail = ({ itemId }) => {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${id}`);
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItem();
  }, [itemId, id]);

  const handleSelect = (selectedIndex) => {
    setActiveIndex(selectedIndex);
  };

  const renderThumbnails = () => {
    const thumbnailData = [
        <img key="itemPhoto1" src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title} />,
        <img key="itemPhoto2" src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title} />,
        <img key="itemPhoto3" src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`} alt={item.title} />,
    ];

    return thumbnailData.map((thumbnail, index) => (
      <li
        key={index}
        data-target="#carouselExample"
        data-slide-to={index}
        className={index === activeIndex ? 'active' : ''}
      >
        <img src={thumbnail} alt={`Thumbnail ${index}`} className="img-fluid" />
      </li>
    ));
  };

  const renderSlides = () => {
    const slideData = [
      <img key="itemPhoto1" src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title} />,
      <img key="itemPhoto2" src={`data:${item.itemPhoto2.photoType};base64,${item.itemPhoto2.data}`} alt={item.title} />,
      <img key="itemPhoto3" src={`data:${item.itemPhoto3.photoType};base64,${item.itemPhoto3.data}`} alt={item.title} />,
    ];
  
    return slideData.map((slide, index) => (
      <div key={index} className={`carousel-item ${index === activeIndex ? 'active' : ''}`}>
        {slide}
      </div>
    ));
  };
  ;

  if (!item) {
    return <div>Loading...</div>;
  }

  return (
    <>
      {/*Carousel Component */}
      <div id="carouselExample" className="carousel slide" data-ride="carousel">
        <ol className="carousel-indicators">{renderThumbnails()}</ol>
        <div className="carousel-inner">{renderSlides()}</div>
        <a
          className="carousel-control-prev"
          href="#carouselExample"
          role="button"
          data-slide="prev"
          onClick={() => handleSelect((activeIndex - 1 + renderSlides().length) % renderSlides().length)}
        >
          <span className="carousel-control-prev-icon" aria-hidden="true"></span>
          <span className="sr-only">Previous</span>
        </a>
        <a
          className="carousel-control-next"
          href="#carouselExample"
          role="button"
          data-slide="next"
          onClick={() => handleSelect((activeIndex + 1) % renderSlides().length)}
        >
          <span className="carousel-control-next-icon" aria-hidden="true"></span>
          <span className="sr-only">Next</span>
        </a>
      </div>

      {/* Your Additional Components */}
      <div className={styles.container}>
        {/* ... */}
      </div>

      {/* Footer */}
      <a href="https://youtu.be/NBIbZIegMRY" target="_blank">
        <footer>
          <div className="texto">
            <span>
              <i className="fab fa-youtube"></i>
              watch on youtube
            </span>
          </div>
        </footer>
      </a>
    </>
  );
};

export default ItemDetail;
