import React, { useState } from 'react';
import axios from 'axios';
import validator from 'validator';
import { useNavigate } from 'react-router-dom';

const ForggotPassword = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    otp: '',
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [resendCooldown, setResendCooldown] = useState(0);
  const [passwordError, setPasswordError] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordMatch, setPasswordMatch] = useState(true);
  const [confirmPasswordError, setConfirmPasswordError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showPassword1, setShowPassword1] = useState(false);

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === 'confirmPassword') {
      setPasswordMatch(formData.password === value);
    }
  };

  const handleSendOTP = async () => {
    try {
      await axios.post('http://localhost:8080/api/v1/otp/sendOTP', {
        email: formData.email,
      });
      setOtpSent(true);
      setResendCooldown(120);
    } catch (error) {
      alert('Failed to send OTP. Please try again.');
    }
  };

  const handleVerifyOTP = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/v1/otp/verifyOTP', {
        email: formData.email,
        otp: parseInt(formData.otp, 10),
      });
      setOtpVerified(true);
    } catch (error) {
      alert('Incorrect OTP entered');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Perform your form submission logic here
      console.log('Form submitted:', formData);
      // Example: await axios.post('/submit', formData);
      navigate('/success'); // Navigate to success page upon successful submission
    } catch (error) {
      console.error('Submission error:', error);
    }
  };

  const togglePasswordVisibility1 = () => {
    setShowPassword1((prevShowPassword) => !prevShowPassword);
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div className="container m-4">
          <div className="row">
            <div className="col-lg-4"></div>
            <div className="col-lg-4">
              <label className="form-label">Email address :</label>
              <input
                className="form-control"
                type="email"
                placeholder="Enter your email"
                value={formData.email}
                onChange={handleChange}
                disabled={otpVerified}
                required
              />
              <div className="mb-3">
                <label>Password</label>
                <div className="row">
                  <div className="col-8">
                    <input
                      type={showPassword ? 'text' : 'password'}
                      className="form-control"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="col-4">
                    <button
                      className="btn btn-outline-secondary"
                      type="button"
                      onClick={togglePasswordVisibility1}
                    >
                      {showPassword1 ? 'Hide' : 'Show'}
                    </button>
                  </div>
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputPassword1" className="form-label">
                    Confirm Password
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    required
                  />
                  {passwordMatch ? null : <p className="text-danger">{confirmPasswordError}</p>}
                </div>
              </div>
            </div>
            <div className="row">
              <div className="container">
                <div className="row">
                  <div className="col-4 m-4">
                    {otpSent && !otpVerified && (
                      <>
                        <label className="px-1">
                          Enter OTP:
                          <input type="text" name="otp" onChange={handleChange} required />
                        </label>
                        <button type="button" onClick={handleVerifyOTP} className="btn btn-warning mx-2">
                          Verify OTP
                        </button>
                      </>
                    )}
                  </div>
                  <div className="col-lg-6 m-4">
                    {!otpVerified && (
                      <>
                        <button
                          type="button"
                          onClick={otpSent ? handleResendOTP : handleSendOTP}
                          disabled={resendCooldown > 0}
                          className="btn btn-warning mx-2"
                        >
                          {otpSent ? 'Resend OTP' : 'Send OTP'}
                        </button>
                        {resendCooldown > 0 && <p>Resend cooldown: {resendCooldown} seconds</p>}
                      </>
                    )}
                  </div>
                </div>
              </div>
              <div className="container">
                <div className="row col-10">
                  {otpVerified && (
                    <button type="submit" className="btn btn-warning m-4">
                      Submit
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    </>
  );
};

export default ForggotPassword;
