import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AddItemForm = () => {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    title: '',
    deviceType: '',
    brand: '',
    description: '',
    price: '',
    phoneNumber: '',
    place: '',
    itemCondition: '',
    model: '',  // Added model field
    photo1: null,
    photo2: null,
    photo3: null,
  });

  const [deviceTypes, setDeviceTypes] = useState([]);
  const [brands, setBrands] = useState([]);

  useEffect(() => {
    // Fetch device types and brands from the backend
    const fetchData = async () => {
      try {
        const deviceTypesResponse = await axios.get('/api/v1/deviceTypes');
        const brandsResponse = await axios.get('/api/v1/brands');

        setDeviceTypes(deviceTypesResponse.data);
        setBrands(brandsResponse.data);
      } catch (error) {
        console.error('Error fetching device types and brands:', error.message);
      }
    };

    fetchData();
  }, []);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const allowedTypes = ['image/jpeg', 'image/png'];

    if (e.target.files.length > 0) {
      const file = e.target.files[0];

      // Validate file type
      if (allowedTypes.includes(file.type)) {
        setFormData({ ...formData, [e.target.name]: file });
      } else {
        alert('Invalid file type. Please upload a JPEG or PNG image.');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('itemDetails', JSON.stringify(formData));
    data.append('itemPhoto1', formData.photo1);
    data.append('itemPhoto2', formData.photo2);
    data.append('itemPhoto3', formData.photo3);

    try {
      await axios.post('/api/v1/item/add', data);
      // Handle success
      console.log('Item and photos saved successfully');
    } catch (error) {
      // Handle error
      console.error('Error saving item and photos:', error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* ... (existing form fields) */}
      <label>Device Type:</label>
      <select name="deviceType" onChange={handleInputChange} value={formData.deviceType}>
        <option value="" disabled>Select device type</option>
        {deviceTypes.map((type) => (
          <option key={type} value={type}>{type}</option>
        ))}
      </select>

      <label>Brand:</label>
      <select name="brand" onChange={handleInputChange} value={formData.brand}>
        <option value="" disabled>Select brand</option>
        {brands.map((brand) => (
          <option key={brand} value={brand}>{brand}</option>
        ))}
      </select>

      <label>Model:</label>
      <input type="text" name="model" onChange={handleInputChange} value={formData.model} />

      {/* ... (existing form fields) */}

      <button type="submit">Submit</button>
    </form>
  );
};

export default AddItemForm;
