import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import  styles from '../CSS/ItemDetails.module.css';
const ItemDetail = ({itemId}) => {
    const [item, setItem] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchItem = async () => {
            try {
              const response = await fetch(`http://localhost:8080/api/v1/item/getItem/${id}`);
              const data = await response.json();
              setItem(data);
            } catch (error) {
              console.error('Error fetching items:', error);
            }
          };
                fetchItem();
    }, [itemId]);

    if (!item) {
        return <div>Loading...</div>;
    }
    

    return (
        <>
        <div id="carouselExampleIndicators" class="carousel slide" data-ride="carousel">
            <ol class="carousel-indicators">
                <li data-target="#carouselExampleIndicators" data-slide-to="0" class="active">
                {/* <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title}/> */}
                </li>
                <li data-target="#carouselExampleIndicators" data-slide-to="1">
                {/* <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title}/> */}
                </li>
                <li data-target="#carouselExampleIndicators" data-slide-to="2">
                {/* <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title}/> */}
                </li>
            </ol>
            <div class="carousel-inner">
                <div class="carousel-item active">
                <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title}/>
                </div>
                <div class="carousel-item">
                <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title}/>
                </div>
                <div class="carousel-item">
                <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title}/>
                </div>
            </div>
            <a class="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev">
                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                <span class="sr-only">Previous</span>
            </a>
            <a class="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next">
                <span class="carousel-control-next-icon" aria-hidden="true"></span>
                <span class="sr-only">Next</span>
            </a>
        </div>

        <div className={styles.container} >
                <div className="box">
                    <div className="product__img img-fluid img-thumbnail">
                    <img class="no-border img-fluid img-thumbnail " src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title} />
                    </div>
        
                    <div className="product__video">
                        <div className="video">
                            <i className="fas fa-play"></i>
                            <p>watch a review</p>
                        </div>
                    </div>
        
                    <div className="product__disc">
                        <div className="product__disc--content">
                            <div className="disc__content--about">
                                <h1>Oculus Rift S</h1>
                                <span>get ready to step into a new world</span>
                                <p>{item.description}</p>
                            </div>
        
                            <div className="product__view--more">
                                <p>view all specifications</p>
                                <div className="view__more--block"></div>
                            </div>
                        </div>
                    </div>
        
                    <div className="product_buttons">
                        <button className="btn wishlist">wishlist</button>
                        <button className="btn buy">buy</button>
                    </div>
                </div>
            </div>
        
        
        
        <a href="https://youtu.be/NBIbZIegMRY" target="_blank"><footer>
            <div className="texto">
                <span>
                    <i className="fab fa-youtube"></i>
                     watch on youtube</span>
            </div>
        </footer>
           </a>
        </>
    );
};

export default ItemDetail;
