import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';


const ItemList = () => {
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/item/seller/7afa80cc-e278-4a38-b41a-9be258b1fe99');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const filteredItems = items.filter(item =>
    item.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Item List</h1>
      <div className='container m-4 p-4'>
        <div className='row'>
          <div className='col-4'></div>
          <div className='col-4'>
            <label htmlFor="search" className='ml-3'>Search by name: </label>
            <input
              type="text"
              id="search"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
            </div>
            <div className='col-4'></div>
        </div>
      </div>
      <ul>
        {filteredItems.map(item => (
          
            <div className='container border border-light bg-dark'>
              <div className='row m-4'>
                <div className='col-2'></div>
                <li key={item.id}><Link to={`/items/${item.id}`} class="link-secondary text-decoration-none">
                  <div className='row m-4'>
                      <div className='col-3'>
                      <img
                        class="img-fluid img-thumbnail"
                        src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                        alt={item.title}
                        // style={{ width: '400px', height: '400px' }}
                      />
                    </div>       
                    <div className='col-3 mr-4'>
                      <h2>{item.title}</h2><br/>
                      <h3>Price: {item.price}</h3>
                    </div> 
                  </div>   
                <div className='col-2'></div>  
               
                  </Link>
                  </li>                    
              </div>
            </div>        
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
