import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../CSS/DartTheme.css';
import NavBar from './NavBar';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Modal from 'react-modal';

const AdminItemAprrove = ({ itemId }) => {
  const { id } = useParams();
    const searchParams = new URLSearchParams(useLocation().search);
    const token = searchParams.get('token');
    const userId = searchParams.get('userId');

    const [item, setItem] = useState(null);

    useEffect(() => {
      const fetchItem = async () => {
        try {
          const response = await fetch(`http://localhost:8080/api/v1/allItems/getItem/${id}`);
          const data = await response.json();
          setItem(data);
        } catch (error) {
          console.error('Error fetching items:', error);
        }
      };
      fetchItem();
    }, [id]);

  const handleDelete = async (itemId) => {
        try {
          const response = await fetch(`http://localhost:8080/api/v1/item/deleteItem/${itemId}`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`,
            },
          });
    
          if (response.ok) {
            console.log('Delete operation successful');
              setTimeout(() => {
                window.location.reload();
              }, 1000);    
              alert('Delete operation successful') 
          } else {
            console.error('Delete operation failed');
            alert('Delete operation failed')
          }
        } catch (error) {
          console.error('Error during delete operation', error);
          alert('Error during delete operation')
        }
      };
  return (
    <div>
      <div>Titile: {item.title}</div>
      <div>Model: {item.model}</div>
      <div>Device Type: {item.deviceType}</div>
      <div>Item Condition: {item.itemCondition}</div>
      <div>Brand: {item.brand}</div>
      <div>Price: {item.price}</div>
      <div>Phone Number: {item.phoneNumber}</div>
      <div>Place: {item.place}</div>
      <div>Description: {item.description}</div>
      <div>Post add date: {item.date}</div>
      <div></div>
    </div>
  )
}

export default AdminItemAprrove
