// RegistrationForm.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RegistrationForm = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    fullName: '',
    nickName: '',
    dob: '',
    gender: '',
    address: '',
    telephone: '',
    email: '',
    password: '',
    confirmPassword: '',
    otp: '',
    otpEmail: '',
  });

  const [isOtpSent, setIsOtpSent] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendOtp();
  };

  const sendOtp = async () => {
    try {
      const response = await fetch('/api/send-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: formData.email }),
      });

      const data = await response.json();

      if (response.ok) {
        setIsOtpSent(true);
      } else {
        console.error(data.message);
      }
    } catch (error) {
      console.error('Error sending OTP:', error);
    }
  };

  const handleOtpVerification = async () => {
    try {
      if (formData.email !== formData.otpEmail) {
        console.error('Email mismatch for OTP verification');
        return;
      }

      const response = await fetch('/api/verify-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          otp: formData.otp,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate('/success');
      } else {
        console.error(data.message);
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
    }
  };

  return (
    <div>
      <h1>Registration Form</h1>
      <form onSubmit={isOtpSent ? handleOtpVerification : handleSubmit}>
        <label>
          Full Name
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Nickname
          <input
            type="text"
            name="nickName"
            value={formData.nickName}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Date of Birth (YYYY-MM-DD)
          <input
            type="text"
            name="dob"
            value={formData.dob}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Gender
          <input
            type="text"
            name="gender"
            value={formData.gender}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Address
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Telephone (10 digits, starts with 0)
          <input
            type="tel"
            name="telephone"
            pattern="[0-9]{10}"
            value={formData.telephone}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Email
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Password
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          Confirm Password
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleInputChange}
            required
          />
        </label>

        <label>
          {isOtpSent ? 'Enter OTP' : 'Email'}
          <input
            type={isOtpSent ? 'text' : 'email'}
            name={isOtpSent ? 'otp' : 'otpEmail'}
            value={isOtpSent ? formData.otp : formData.otpEmail}
            onChange={handleInputChange}
            required
          />
        </label>

        <button type="submit">
          {isOtpSent ? 'Verify OTP' : 'Send OTP'}
        </button>
      </form>
    </div>
  );
};

export default RegistrationForm;
