import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../CSS/DartTheme.css';
import Modal from 'react-modal';
import { useNavigate } from 'react-router-dom';

const AdminSliderAds = () => {
const searchParams = new URLSearchParams(useLocation().search);
  const token = searchParams.get('token');
  const adminId = searchParams.get('adminId');
  const navigate = useNavigate();
    const [items, setItems] = useState([]);
    useEffect(() => {
        // Fetch items from the backend API
        const fetchItems = async () => {
            const yourBearerToken = token;
          try {
            const response = await fetch(`http://localhost:8080/api/v1/admin/getSliderPhoto`, {
                headers: {
                  'Authorization': `Bearer ${yourBearerToken}`,
                  'Content-Type': 'application/json',
                },
              });
            const data = await response.json();
            setItems(data);
          } catch (error) {
            console.error('Error fetching items:', error);
          }
        };

        fetchItems();
      }, []);

      const [isModalOpen, setIsModalOpen] = useState(false);

  const handleButtonClick = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };
  const editPost = (itemId) => {
    navigate(`/editPost?token=${token}&userId=${adminId}&itemId=${itemId}`);
  };


  const modalStyle = {
    content: {
      width: '50%', 
      top: isModalOpen ? '50%' : '-100%', 
      left: '50%',
      transform: 'translate(-50%, -50%)',
      transition: 'top 0.3s ease-in-out', 
      zIndex: 2000, 
      border: '10px solid #FFD700',
      opacity:0.75,
      fontWeight: 'bold',
      borderRadius: '10px',
      padding: '20px',
      position: 'relative',
      
    },
    overlay: {
      backgroundColor: 'rgba(0, 0, 0, 0.5)', 
      transition: 'background-color 0.3s ease-in-out', 
    },
  };

  const handleDelete = async (itemId) => {
    const token = token;
    const userId =userId;
    try {
      const response = await fetch(`http://localhost:8080/api/v1/item/deleteItem/${itemId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },body: JSON.stringify({userId})
      });

      if (response.ok) {
        console.log('Delete operation successful');
          setTimeout(() => {
            window.location.reload();
          }, 1000);    
          alert('Delete operation successful') 
      } else {
        console.error('Delete operation failed');
        alert('Delete operation failed')
      }
    } catch (error) {
      console.error('Error during delete operation', error);
      alert('Error during delete operation')
    }
  };
  const [selectedFile, setSelectedFile] = useState(null);
  const [submitEnabled, setSubmitEnabled] = useState(false);

  const handleFileInputChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setSubmitEnabled(true);
  };

  const handleRemoveImage = () => {
    setSelectedFile(null);
    setSubmitEnabled(false);
  };

  const handleSubmit = () => {
    console.log('Submit action triggered');
  };
  return (
    <div>
    <div>
      <label htmlFor="fileInput" className="add-slider-button">
        Add Slider Photos
      </label>
      <input
        type="file"
        id="fileInput"
        accept="image/*"  // Specify accepted file types (images in this case)
        onChange={handleFileInputChange}
        style={{ display: 'none' }} // Hide the file input element
      />
      {selectedFile && (
        <div className="image-preview">
          <img src={URL.createObjectURL(selectedFile)} alt="Selected" style={{ width: 200, height: 200 }} />
          <button onClick={handleRemoveImage} className="remove-button">
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
      )}
      {submitEnabled && (
        <button onClick={handleSubmit} className="submit-button">
          Submit
        </button>
      )}
    </div>
          <ul type='none'>
        {items.length !== 0 ?items.map(item => (
          <div className='container-fluid border border-light m-4 view overlay zoom' key={item.id}>
            <div className='row m-4'>

              <li key={item.id}>
                <Link to={`/items/${item.id}`} className="text-light text-decoration-none">
                  <div className='row m-4'>

                    <div className='col-lg-3 col-md-4'>
                      <img
                        className="no-border img-fluid img-thumbnail bg-image hover-zoom"
                        src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                        alt={item.title}
                      />
                    </div>
                  </div>
                  </Link>
                  <div className='col-2'>
                  </div>               
              </li>
              <div className='row'>
                <div className='col-lg-10'></div>
                <div className='col-1'>
                <button className='btn btn-warning' onClick={() => editPost(item.id)}>Edit</button>
                </div>
                <div className='col-1'>
                <button className='btn btn-warning'onClick={handleButtonClick}>Delete</button>
                  <Modal
                  className='bg-dark'
                    shouldCloseOnOverlayClick={true}
                    isOpen={isModalOpen}
                    shouldCloseOnEsc={true}
                    onRequestClose={closeModal}
                    contentLabel="Example Modal"
                    style={modalStyle}>
                          <div className='fs-1 text-info bg-dark text-center '>Warning!</div>
                          <div className='fs-4 text-info bg-dark text-center my-3'>Are you sure to delete this post, which title is {item.title}?</div>
                          <label> Why are you delete this :  </label>           

                        <div className='row bg-dark my-3'>
                        <div className='col-4'>
                        </div>
                        <div className='col-2 '>
                          <button onClick={() => { handleDelete(item.id); closeModal(); }}>Yes</button>
                        </div>
                        <div className='col-2 '>
                          <button onClick={closeModal}>No</button>
                        </div>                    
                    </div>                 
                  </Modal>
                </div>
              </div>
            </div>
          </div>
        )): <li><div className='fs-1 '><center>"Currently, no any advertisement found"</center></div></li>}
      </ul>
    </div>
  )
}

export default AdminSliderAds
