import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RegistrationForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    nickName: '',
    dob: '',
    gender: '',
    address: '',
    telephone: '',
    email: '',
    password: '',
    confirmPassword: '',
    otp: '',
  });

  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [resendCooldown, setResendCooldown] = useState(0);
  const[email,setEmail] = useState('');

  const handleChange = (e) => { 
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  useEffect(() => {
         setEmail(formData.email); 
  },[formData.email != '']);
  
  const handleSendOTP = async () => {
    // Email validation
    if (!validator.isEmail(email)) {
      alert('Invalid email format');
      return;
    }

    try {
      await axios.post('http://localhost:8080/api/v1/otp/sendOTP', { email });
      setOtpSent(true);
      setResendCooldown(120);
    } catch (error) {
      // Handle error with meaningful message to the user
      alert('Failed to send OTP. Please try again.');
    }
  };

  const handleResendOTP = async () => {
    try {
      await axios.post('http://localhost:8080/api/v1/otp/reSendOTP', {
        email: formData.email,
      });
      setResendCooldown(120); // Reset the cooldown to 2 minutes
    } catch (error) {
      // Handle error
    }
  };

  const handleVerifyOTP = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/v1/otp/verifyOTP', {
        email: formData.email,
        otp: parseInt(formData.otp, 10),
      });
      setOtpVerified(true);
    } catch (error) {
      // Handle incorrect OTP error
      alert('Incorrect OTP entered');
    }
  };

  const handleSubmit = async () => {
    try {
      // Add additional frontend validations as needed
      if (otpVerified) {
        const response = await axios.post('http://localhost:8080/api/v1/register', formData);
        // Handle response as needed, e.g., navigate to success page
        console.log(response.data);
      }
    } catch (error) {
      // Handle error, e.g., show pop-up with "registration unsuccessful" message
      console.error(error);
    }
  };

  useEffect(() => {
    let interval;
    if (resendCooldown > 0) {
      interval = setInterval(() => {
        setResendCooldown((prev) => Math.max(prev - 1, 0));
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [resendCooldown]);

  return (
    <form>
      <label>
        Full Name:
        <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} />
      </label>
      <label>
        Nick Name:
        <input type="text" name="nickName" value={formData.nickName} onChange={handleChange} />
      </label>
      <label>
        Date of Birth (YYYY-MM-DD):
        <input type="text" name="dob" value={formData.dob} onChange={handleChange} />
      </label>
      <label>
        Gender:
        <input type="text" name="gender" value={formData.gender} onChange={handleChange} />
      </label>
      <label>
        Address:
        <input type="text" name="address" value={formData.address} onChange={handleChange} />
      </label>
      <label>
        Telephone (10 digits, starts with 0):
        <input type="text" name="telephone" value={formData.telephone} onChange={handleChange} />
      </label>
      <label>
        Email:
        <input type="text" name="email" value={formData.email} onChange={handleChange} />
      </label>
      <label>
        Password:
        <input type="password" name="password" required value={formData.password} onChange={handleChange} />
      </label>
      <label>
        Confirm Password:
        <input
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
        />
      </label>
      <button type="button" onClick={otpSent ? handleResendOTP : handleSendOTP} disabled={resendCooldown > 0}>
        {otpSent ? 'Resend OTP' : 'Send OTP'}
      </button>
      {otpSent && (
        <>
          <label>
            Enter OTP:
            <input type="text" name="otp" onChange={handleChange} />
          </label>
          <button type="button" onClick={handleVerifyOTP}>
            Verify OTP
          </button>
        </>
      )}
      {otpVerified && (
        <button type="button" onClick={handleSubmit}>
          Submit
        </button>
      )}
      {resendCooldown > 0 && <p>Resend cooldown: {resendCooldown} seconds</p>}
    </form>
  );
};

export default RegistrationForm;
