import React, { useState } from 'react';

const ItemForm = () => {
  const [formData, setFormData] = useState({
    itemName: '',
    place: '',
    itemType: '',
    itemPhoto: null,
    condition: '',
    description: '',
    contactNumber: '',
    brand: '',
    model: '',
    itemPrice: '',
  });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setFormData({ ...formData, itemPhoto: file });

    // You can also preview the image here
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      // Update your image preview state if needed
    };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Perform image resizing here (you may need a separate library)

    const formDataForBackend = new FormData();
    for (const key in formData) {
      formDataForBackend.append(key, formData[key]);
    }

    // Send the form data to the backend using fetch or axios
    try {
      const response = await fetch('http://localhost:8080/api/items', {
        method: 'POST',
        body: formDataForBackend,
      });

      // Handle the response as needed
      console.log(response);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Render your form fields here */}
      <input type="file" name="itemPhoto" onChange={handleImageChange} />
      {/* Add other input fields */}
      <label>Item Name:</label>
      <input type="text" name="itemName" value={itemDetails.itemName} onChange={handleChange} />

      <label>Place:</label>
      <input type="text" name="place" value={itemDetails.place} onChange={handleChange} />

      <label>Item Type:</label>
      <input type="text" name="itemType" value={itemDetails.itemType} onChange={handleChange} />

      <label>Item Photo URL:</label>
      <input type="text" name="itemPhoto" value={itemDetails.itemPhoto} onChange={handleChange} />

      <label>Condition:</label>
      <input type="text" name="condition" value={itemDetails.condition} onChange={handleChange} />

      <label>Description:</label>
      <textarea name="description" value={itemDetails.description} onChange={handleChange}></textarea>

      <label>Contact Number:</label>
      <input type="text" name="contactNumber" value={itemDetails.contactNumber} onChange={handleChange} />

      <label>Brand:</label>
      <input type="text" name="brand" value={itemDetails.brand} onChange={handleChange} />

      <label>Model:</label>
      <input type="text" name="model" value={itemDetails.model} onChange={handleChange} />

      <label>Price:</label>
      <input type="text" name="price" value={itemDetails.price} onChange={handleChange} />

      <button type="submit">Submit</button>
      <button type="submit">Submit</button>
    </form>
  );
};

export default ItemForm;
