import React, { useEffect, useState } from 'react'

const AdsSlider = () => {
    
    const [items, setItems] = useState([]); 
    useEffect(() => {
        // Fetch items from the backend API
        const fetchItems = async () => {
          try {
            const response = await fetch(`http://localhost:8080/api/v1/allItems/getSliderPhoto`, {
                headers: {
                  'Content-Type': 'application/json',
                },
              });
            const data = await response.json();
            setItems(data);
          } catch (error) {
            console.error('Error fetching items:', error);
          }
        };

        fetchItems();
      }, []);
  return (
    <>
        <div className='container border border-warning '>
        <div className='row'>
          <div className='col-lg-12 col-md-12 position-relative p-4 m-4'>
            <div id="carouselSliderOfItem" className="carousel carousel-dark slide">
              <div className="carousel-indicators mt-4 mx-2">
                {items.map((photo, index) => (
                  <button   type="button" key={index} data-bs-target="#carouselSliderOfItem" data-bs-slide-to={index} className={index === 0 ? "active thumbnail mx-2 " : "btn thumbnail mx-2"} aria-label={`Slide ${index + 1}`}>
                    <img className="d-block w-100 border border-dark border-4  " src={`data:${photo.photoType};base64,${photo.data}`} alt={photo.name} />
                  </button>
                ))}
              </div>
              <div className="carousel-inner">
                {items.map((photo, index) => (
                  <div key={index} className={index === 0 ? "carousel-item active" : "carousel-item"}>
                    <img className="d-block w-100" src={`data:${photo.photoType};base64,${photo.data}`} alt={photo.name}  style={{ width: '100%', maxHeight: '100%', cursor: 'pointer',objectFit: 'cover' }} />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
    </div>
    
    </>
  )
}

export default AdsSlider
