import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../CSS/DartTheme.css';
import NavBar from './NavBar';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState([]);

  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/item/seller/7afa80cc-e278-4a38-b41a-9be258b1fe99');
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const generateSearchVariations = (term) => {
    const variations = [];
    const cleanedTerm = term.replace(/[^\w\s]/gi, ''); // Remove symbols

    // Original term
    variations.push(cleanedTerm);

    // Remove spaces
    variations.push(cleanedTerm.replace(/\s/g, ''));

    // Split by spaces and join without spaces
    variations.push(cleanedTerm.split(/\s/).join(''));

    return variations;
  };

  const handleSearch = () => {
    // Generate search term variations
    const searchVariations = generateSearchVariations(searchTerm.toLowerCase());

    // Filter items based on the cleaned search term
    const filteredItems = items.filter(item => {
      const cleanedTitle = item.title.toLowerCase().replace(/[^\w\s]/gi, ''); // Remove symbols
      return searchVariations.some(variation => cleanedTitle.includes(variation));
    });

    setSearchResult(filteredItems);
  };

  const handleInputChange = (e) => {
    const newSearchTerm = e.target.value;
    setSearchTerm(newSearchTerm);

    // Automatic filtering as the user types
    const cleanedSearchTerm = newSearchTerm.toLowerCase().replace(/[^\w\s]/gi, ''); // Remove symbols
    const filteredItems = items.filter(item => {
      const cleanedTitle = item.title.toLowerCase().replace(/[^\w\s]/gi, ''); // Remove symbols
      return cleanedTitle.includes(cleanedSearchTerm);
    });

    setSearchResult(filteredItems);
  };

  const textStyle = {
    color: '#FF9400',
    fontWeight: 'bold'
  };

  const containerStyle = {
    cursor: 'none', 
  };

  return (
    <div>
      
      <div className='container'><h1>Item List</h1> </div>
      <div className='container'><NavBar/></div>
      <div className='container m-4' style={containerStyle}>
        <div className='row'>
          <div className='col-2'></div>
          <div className='col-6'>
            <label htmlFor="search" className='m-3' style={textStyle}>Search by name: </label>
            <input
              placeholder="Search" 
              aria-label="Search"
              type="text"
              id="search"
              value={searchTerm}
              onChange={handleInputChange}
            />
            <button className="btn m-2 my-sm-0 btn-outline-secondary" type="button" onClick={handleSearch}  style={textStyle}>
              Search
            </button>
          </div>
          <div className='col-4'></div>
        </div>
      </div>
      <ul>
        {(searchResult.length > 0 ? searchResult : items).map(item => (
          <div className='container border border-light' key={item.id}>
            <div className='row m-4'>
              <div className='col-2'></div>
              <li key={item.id}><Link to={`/items/${item.id}`} class="text-light text-decoration-none">
                  <div className='row m-4'>
                      <div className='col-3'>
                      <img
                        class="img-fluid img-thumbnail"
                        src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`}
                        alt={item.title}
                      />
                    </div>
                    <div className='col-2'></div>       
                    <div className='col-3 mr-4'>
                      <h2 className='text-center'style={textStyle}>{item.title}</h2><br/>
                      <h4 >Price: Rs.{item.price}</h4><br/>
                      <h5>Place: {item.place}</h5>
                    </div> 
                  </div>   
                <div className='col-2'></div>  
               
                  </Link>
                  </li>    
            </div>
          </div>
        ))}
      </ul>
      {searchResult.length === 0 && searchTerm !== '' && (
        <p style={textStyle}>No results found for "{searchTerm}"</p>
      )}
    </div>
  );
};

export default ItemList;
