import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { imageFileResizer } from 'react-image-file-resizer';

const ItemList = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch items from the backend API
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/item/seller/7afa80cc-e278-4a38-b41a-9be258b1fe99');
        const data = await response.json();

        // Resize images before setting them in the state
        const resizedItems = await Promise.all(
          data.map(async item => {
            const resizedImage = await resizeImage(item.itemPhoto1.data, 100, 100); // Adjust the dimensions as needed
            return { ...item, itemPhoto1: { ...item.itemPhoto1, data: resizedImage } };
          })
        );

        setItems(resizedItems);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };

    fetchItems();
  }, []);

  const resizeImage = (imageData, newWidth, newHeight) => {
    return new Promise(resolve => {
      imageFileResizer(
        imageData,
        newWidth,
        newHeight,
        'JPEG',
        100,
        0,
        uri => {
          resolve(uri);
        },
        'base64'
      );
    });
  };

  return (
    <div>
      <h1>Item List</h1>
      <ul>
        {items.map(item => (
          <li key={item.id}>
            <Link to={`/items/${item.id}`}>
              <img src={`data:${item.itemPhoto1.photoType};base64,${item.itemPhoto1.data}`} alt={item.title} />
              <p>{item.title}</p>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
